import { useEffect, useState } from "react";

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [section, setSection] = useState("MOVIES");

  // Auth
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // Data
  const [movies, setMovies] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [sessions, setSessions] = useState([]);

  // Session form
  const [movieId, setMovieId] = useState("");
  const [roomId, setRoomId] = useState("");
  const [dateTime, setDateTime] = useState("");

  // ===== LOGIN =====
  const login = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/api/users/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    })
      .then(res => res.json())
      .then(user => setCurrentUser(user))
      .catch(() => setError("Login incorrecto"));
  };

  const logout = () => {
    setCurrentUser(null);
    setSidebarOpen(false);
  };

  // ===== LOAD DATA =====
  useEffect(() => {
    if (currentUser) {
      fetch("http://localhost:8080/api/movies").then(r => r.json()).then(setMovies);
      fetch("http://localhost:8080/api/rooms").then(r => r.json()).then(setRooms);
      fetch("http://localhost:8080/api/sessions").then(r => r.json()).then(setSessions);
    }
  }, [currentUser]);

  // ===== CREATE SESSION =====
  const createSession = (e) => {
    e.preventDefault();

    fetch(`http://localhost:8080/api/sessions?movieId=${movieId}&roomId=${roomId}&dateTime=${dateTime}`, {
      method: "POST"
    }).then(() => {
      setMovieId("");
      setRoomId("");
      setDateTime("");
      fetch("http://localhost:8080/api/sessions")
        .then(r => r.json())
        .then(setSessions);
    });
  };

  // ===== LOGIN UI =====
  if (!currentUser) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <form onSubmit={login} className="bg-white p-6 rounded shadow w-80">
          <h2 className="text-xl font-bold mb-4">Login</h2>
          {error && <p className="text-red-500">{error}</p>}
          <input className="border p-2 w-full mb-2" placeholder="Usuario" onChange={e => setUsername(e.target.value)} />
          <input className="border p-2 w-full mb-4" type="password" placeholder="Contrase√±a" onChange={e => setPassword(e.target.value)} />
          <button className="bg-blue-600 text-white w-full p-2 rounded">Entrar</button>
        </form>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-gray-900 text-white p-4 flex justify-between">
        <button onClick={() => setSidebarOpen(!sidebarOpen)}>‚ò∞</button>
        <span>{currentUser.username}</span>
      </header>

      {sidebarOpen && (
        <aside className="fixed top-0 left-0 h-full w-64 bg-gray-800 text-white p-4">
          <div onClick={() => setSection("MOVIES")} className="p-2 hover:bg-gray-700">Pel√≠culas</div>
          <div onClick={() => setSection("SESSIONS")} className="p-2 hover:bg-gray-700">Sesiones</div>
          <div onClick={logout} className="p-2 bg-red-600 mt-6">Cerrar sesi√≥n</div>
        </aside>
      )}

      <main className="p-6">
        {section === "SESSIONS" && (
          <>
            <h2 className="text-2xl font-bold mb-4">Sesiones</h2>

            {currentUser.role === "ADMIN" && (
              <form onSubmit={createSession} className="bg-white p-4 rounded shadow mb-6">
                <select className="border p-2 w-full mb-2" value={movieId} onChange={e => setMovieId(e.target.value)}>
                  <option value="">Pel√≠cula</option>
                  {movies.map(m => <option key={m.id} value={m.id}>{m.title}</option>)}
                </select>

                <select className="border p-2 w-full mb-2" value={roomId} onChange={e => setRoomId(e.target.value)}>
                  <option value="">Sala</option>
                  {rooms.map(r => <option key={r.id} value={r.id}>{r.name}</option>)}
                </select>

                <input className="border p-2 w-full mb-2" type="datetime-local"
                  value={dateTime} onChange={e => setDateTime(e.target.value)} />

                <button className="bg-blue-600 text-white px-4 py-2 rounded">Crear sesi√≥n</button>
              </form>
            )}

            <ul>
              {sessions.map(s => (
                <li key={s.id} className="bg-white p-3 mb-2 rounded shadow">
                  üé¨ {s.movie.title} ‚Äî üè¢ {s.room.name} ‚Äî üìÖ {s.dateTime}
                </li>
              ))}
            </ul>
          </>
        )}
      </main>
    </div>
  );
}

export default App;
