import { useState } from "react";

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  // UI
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [section, setSection] = useState("MOVIES");
  const [isRegister, setIsRegister] = useState(false);

  // Auth
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // LOGIN
  const handleLogin = (e) => {
    e.preventDefault();

    fetch("http://localhost:8080/api/users/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    })
      .then(res => {
        if (!res.ok) throw new Error();
        return res.json();
      })
      .then(user => {
        setCurrentUser(user);
        setError("");
      })
      .catch(() => setError("Usuario o contraseña incorrectos"));
  };

  // REGISTRO
  const handleRegister = (e) => {
    e.preventDefault();

    fetch("http://localhost:8080/api/users/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    })
      .then(res => res.json())
      .then(() => {
        setIsRegister(false);
        setError("Usuario creado, ahora puedes iniciar sesión");
      });
  };

  const logout = () => {
    setCurrentUser(null);
    setSidebarOpen(false);
    setUsername("");
    setPassword("");
  };

  // ===== LOGIN / REGISTRO =====
  if (!currentUser) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <form
          onSubmit={isRegister ? handleRegister : handleLogin}
          className="bg-white p-6 rounded shadow w-80"
        >
          <h2 className="text-xl font-bold mb-4">
            {isRegister ? "Registro" : "Login"}
          </h2>

          {error && <p className="text-red-500 mb-2">{error}</p>}

          <input
            className="border p-2 w-full mb-2"
            placeholder="Usuario"
            value={username}
            onChange={e => setUsername(e.target.value)}
            required
          />

          <input
            className="border p-2 w-full mb-4"
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
          />

          <button className="bg-blue-600 text-white w-full p-2 rounded mb-2">
            {isRegister ? "Crear cuenta" : "Entrar"}
          </button>

          <p
            className="text-sm text-center text-blue-600 cursor-pointer"
            onClick={() => {
              setIsRegister(!isRegister);
              setError("");
            }}
          >
            {isRegister
              ? "¿Ya tienes cuenta? Inicia sesión"
              : "¿No tienes cuenta? Regístrate"}
          </p>
        </form>
      </div>
    );
  }

  // ===== APP =====
  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-gray-900 text-white p-4 flex justify-between">
        <button onClick={() => setSidebarOpen(!sidebarOpen)}>☰</button>
        <span>{currentUser.username} ({currentUser.role})</span>
      </header>

      {sidebarOpen && (
        <aside className="fixed top-0 left-0 h-full w-64 bg-gray-800 text-white p-4">
          <div onClick={() => setSection("MOVIES")} className="p-2 hover:bg-gray-700 cursor-pointer">
            Películas
          </div>
          <div onClick={logout} className="p-2 bg-red-600 mt-6 cursor-pointer">
            Cerrar sesión
          </div>
        </aside>
      )}

      <main className="p-6">
        <h2 className="text-2xl font-bold">Bienvenido a CineVerse</h2>
      </main>
    </div>
  );
}

export default App;
