import React, { useState } from "react";
import { postData, deleteData, fetchData } from "../../utils/api";
import logo from "../common/LOGO_CineVerse.png";

export default function LoginForm({ onLoginSuccess, isRegister, setIsRegister }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    postData("http://localhost:8080/api/users/login", { username, password })
      .then(res => {
        if (!res.ok) throw new Error();
        return res.json();
      })
      .then(user => {
        onLoginSuccess(user);
        setError("");
      })
      .catch(() => setError("Credenciales incorrectas"));
  };

  const handleRegister = (e) => {
    e.preventDefault();
    postData("http://localhost:8080/api/users/register", { username, password })
      .then(() => {
        setIsRegister(false);
        setError("Usuario creado, ahora inicia sesión");
      });
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-gray-900 to-gray-800 p-4">
      <form onSubmit={isRegister ? handleRegister : handleLogin}
        className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <div className="text-center mb-6">
          <img
            src={logo}
            alt="CineVerse"
            className="w- h-32 mx-auto mb-4"
          />
          <h1 className="text-3xl font-bold text-gray-900">CineVerse</h1>
        </div>

        <h2 className="text-xl font-bold mb-4 text-center text-gray-700">
          {isRegister ? "Crear Cuenta" : "Iniciar Sesión"}
        </h2>
        
        {error && <p className="text-red-500 mb-3 p-2 bg-red-100 rounded text-sm">{error}</p>}
        
        <input className="border border-gray-300 p-3 w-full mb-3 rounded focus:outline-none focus:border-blue-600"
          placeholder="Usuario"
          value={username}
          onChange={e => setUsername(e.target.value)}
          required />
        
        <input className="border border-gray-300 p-3 w-full mb-4 rounded focus:outline-none focus:border-blue-600"
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required />
        
        <button className="bg-blue-600 hover:bg-blue-700 text-white w-full p-3 rounded font-semibold mb-3 transition-colors">
          {isRegister ? "Crear cuenta" : "Entrar"}
        </button>
        
        <p className="text-sm text-center text-blue-600 cursor-pointer hover:text-blue-800 transition-colors"
          onClick={() => { setIsRegister(!isRegister); setError(""); }}>
          {isRegister ? "¿Ya tienes cuenta? Inicia sesión" : "¿No tienes cuenta? Crea una"}
        </p>
      </form>
    </div>
  );
}
