import { useEffect, useState } from "react";

function App() {
  // ===== AUTH =====
  const [currentUser, setCurrentUser] = useState(null);
  const [isRegister, setIsRegister] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // ===== UI =====
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [section, setSection] = useState("MOVIES");

  // ===== DATA =====
  const [movies, setMovies] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [sessions, setSessions] = useState([]);

  // ===== SESSION FORM =====
  const [movieId, setMovieId] = useState("");
  const [roomId, setRoomId] = useState("");
  const [dateTime, setDateTime] = useState("");

const [showSessionConfirm, setShowSessionConfirm] = useState(false);
const [sessionToDelete, setSessionToDelete] = useState(null);


  // ===== MOVIE FORM =====
  const [newMovieTitle, setNewMovieTitle] = useState("");
  const [newMovieDescription, setNewMovieDescription] = useState("");
  const [newMovieDuration, setNewMovieDuration] = useState("");
  const [newMovieImageUrl, setNewMovieImageUrl] = useState("");

const [showMovieConfirm, setShowMovieConfirm] = useState(false);
const [movieToDelete, setMovieToDelete] = useState(null);

  // ===== ROOM FORM =====
  const [newRoomName, setNewRoomName] = useState("");
  const [newRoomSeats, setNewRoomSeats] = useState("");

const [showRoomConfirm, setShowRoomConfirm] = useState(false);
const [roomToDelete, setRoomToDelete] = useState(null);


  // ===== TICKETS =====
  const [selectedSession, setSelectedSession] = useState(null);
  const [takenSeats, setTakenSeats] = useState([]);
const [showSeatModal, setShowSeatModal] = useState(false);

// ===== SEAT INFO =====
const [selectedSeat, setSelectedSeat] = useState(null);
const [seatInfo, setSeatInfo] = useState("");
const [newSeat, setNewSeat] = useState("");

const [confirmAction, setConfirmAction] = useState(null); 

// ===== STAR WARS =====
const [swMovies, setSwMovies] = useState([]);
const [loadingSw, setLoadingSw] = useState(false);

const movieExists = (title) => {
  return movies.some(m => m.title.toLowerCase() === title.toLowerCase());
};

  // ===== HELPERS =====
  const formatDateTime = (dateTimeString) => {
    const date = new Date(dateTimeString);
    return date.toLocaleString("es-ES", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
      hour: "2-digit",
      minute: "2-digit"
    });
  };

  // ===== LOGIN =====
  const login = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/api/users/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    })
      .then(res => {
        if (!res.ok) throw new Error();
        return res.json();
      })
      .then(user => {
        setCurrentUser(user);
        setError("");
      })
      .catch(() => setError("Credenciales incorrectas"));
  };

  // ===== REGISTER =====
  const register = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/api/users/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    }).then(() => {
      setIsRegister(false);
      setError("Usuario creado, ahora inicia sesión");
    });
  };

  // ===== LOGOUT =====
  const logout = () => {
    setCurrentUser(null);
    setSidebarOpen(false);
    setUsername("");
    setPassword("");
  };

useEffect(() => {
  if (!selectedSession) return;

  const interval = setInterval(() => {
    fetch(`http://localhost:8080/api/tickets/session/${selectedSession.id}`)
      .then(r => r.json())
      .then(data => setTakenSeats(data.map(t => t.seatNumber)));
  }, 3000); // cada 3 segundos

  return () => clearInterval(interval);
}, [selectedSession]);

  // ===== LOAD DATA =====
  useEffect(() => {
    if (currentUser) {
      fetch("http://localhost:8080/api/movies").then(r => r.json()).then(setMovies);
      fetch("http://localhost:8080/api/rooms").then(r => r.json()).then(setRooms);
      fetch("http://localhost:8080/api/sessions").then(r => r.json()).then(setSessions);
    }
  }, [currentUser]);

  // ===== CREATE SESSION =====
  const createSession = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:8080/api/sessions?movieId=${movieId}&roomId=${roomId}&dateTime=${dateTime}`,
      { method: "POST" }
    ).then(() => {
      setMovieId("");
      setRoomId("");
      setDateTime("");
      fetch("http://localhost:8080/api/sessions")
        .then(r => r.json())
        .then(setSessions);
    });
  };

  // ===== LOGIN UI =====
  if (!currentUser) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <form onSubmit={isRegister ? register : login}
          className="bg-white p-6 rounded shadow w-80">
          <h2 className="text-xl font-bold mb-4">
            {isRegister ? "Registro" : "Login"}
          </h2>
          {error && <p className="text-red-500 mb-2">{error}</p>}
          <input className="border p-2 w-full mb-2"
            placeholder="Usuario"
            value={username}
            onChange={e => setUsername(e.target.value)} />
          <input className="border p-2 w-full mb-4"
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={e => setPassword(e.target.value)} />
          <button className="bg-blue-600 text-white w-full p-2 rounded mb-2">
            {isRegister ? "Crear cuenta" : "Entrar"}
          </button>
          <p className="text-sm text-center text-blue-600 cursor-pointer"
            onClick={() => { setIsRegister(!isRegister); setError(""); }}>
            {isRegister ? "¿Ya tienes cuenta?" : "¿No tienes cuenta?"}
          </p>
        </form>
      </div>
    );
  }

  // ===== APP =====
  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-gray-900 text-white p-4 flex justify-between items-center fixed top-0 left-0 w-full z-40">
        <button onClick={() => setSidebarOpen(!sidebarOpen)}>☰</button>
        <span>{currentUser.username} ({currentUser.role})</span>
      </header>

      {sidebarOpen && (
        <aside className="fixed top-0 left-0 h-full w-64 bg-gray-800 text-white p-4 z-50">
          <h2 className="text-xl font-bold mb-6">CineVerse</h2>

          <div className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => { setSection("MOVIES"); setSidebarOpen(false); }}>
            Películas
          </div>

          <div className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => { setSection("ROOMS"); setSidebarOpen(false); }}>
            Salas
          </div>

          <div className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => { setSection("SESSIONS"); setSidebarOpen(false); }}>
            Sesiones
          </div>

          <div className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => { setSection("TICKETS"); setSidebarOpen(false); }}>
            Entradas
          </div>

	{currentUser.role === "ADMIN" && (
  <div
    className="p-2 hover:bg-gray-700 cursor-pointer"
    onClick={() => { setSection("STARWARS"); setSidebarOpen(false); }}
  >
    Star Wars
  </div>
)}


          <div className="p-2 bg-red-600 mt-6 cursor-pointer"
            onClick={logout}>
            Cerrar sesión
          </div>

        </aside>
      )}

      <main className="p-6 pt-24">

        {/* ===== MOVIES ===== */}
{section === "MOVIES" && (
  <>
    <h2 className="text-2xl font-bold mb-4">Películas</h2>

    {currentUser.role === "ADMIN" && (
      <form
        className="bg-white p-4 rounded shadow mb-6"
        onSubmit={e => {
          e.preventDefault();

          fetch("http://localhost:8080/api/movies", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              title: newMovieTitle,
              description: newMovieDescription,
              duration: newMovieDuration,
              imageUrl: newMovieImageUrl
            })
          })
            .then(res => {
              if (!res.ok) {
                throw new Error("La película ya existe");
              }
            })
            .then(() => {
              alert("Película añadida correctamente");
              setNewMovieTitle("");
              setNewMovieDescription("");
              setNewMovieDuration("");
              setNewMovieImageUrl("");

              fetch("http://localhost:8080/api/movies")
                .then(r => r.json())
                .then(setMovies);
            })
            .catch(err => alert(err.message));
        }}
      >
        <h3 className="font-bold mb-2">Añadir película</h3>

        <input
          className="border p-2 w-full mb-2"
          placeholder="Título"
          value={newMovieTitle}
          onChange={e => setNewMovieTitle(e.target.value)}
        />

        <input
          className="border p-2 w-full mb-2"
          type="number"
          placeholder="Duración"
          value={newMovieDuration}
          onChange={e => setNewMovieDuration(e.target.value)}
        />

        <input
          className="border p-2 w-full mb-2"
          placeholder="URL imagen"
          value={newMovieImageUrl}
          onChange={e => setNewMovieImageUrl(e.target.value)}
        />

        <textarea
          className="border p-2 w-full mb-2"
          placeholder="Descripción"
          value={newMovieDescription}
          onChange={e => setNewMovieDescription(e.target.value)}
        />

        <button className="bg-blue-600 text-white px-4 py-2 rounded">
          Añadir película
        </button>
      </form>
    )}

    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
      {movies.map(movie => (
        <div
          key={movie.id}
          className="bg-white rounded shadow overflow-hidden"
        >
          {movie.imageUrl && (
            <img
              src={movie.imageUrl}
              alt={movie.title}
              className="w-full h-48 object-cover"
            />
          )}

          <div className="p-4">
            <h3 className="text-xl font-bold">{movie.title}</h3>
            <p className="text-sm text-gray-600">
              {movie.duration} min
            </p>
            <p className="mt-2">{movie.description}</p>

            {currentUser.role === "ADMIN" && (
              <button
                className="bg-red-500 text-white px-3 py-1 rounded mt-3"
                onClick={() => {
                  setMovieToDelete(movie);
                  setShowMovieConfirm(true);
                }}
              >
                Eliminar
              </button>
            )}
          </div>
        </div>
      ))}
    </div>

    {/* ===== MODAL CONFIRMACIÓN ELIMINAR ===== */}
    {showMovieConfirm && movieToDelete && (
      <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg shadow-lg p-6 w-96 text-center">
          <h3 className="text-xl font-bold mb-4">
            Eliminar película
          </h3>

          <p className="mb-6">
            ¿Seguro que quieres eliminar<br />
            <span className="font-semibold">
              {movieToDelete.title}
            </span>?
          </p>

          <div className="flex gap-4">
            <button
              className="bg-red-600 text-white px-4 py-2 rounded w-full"
              onClick={() => {
                fetch(
                  `http://localhost:8080/api/movies/${movieToDelete.id}`,
                  { method: "DELETE" }
                ).then(() => {
                  setMovies(
                    movies.filter(m => m.id !== movieToDelete.id)
                  );
                  setShowMovieConfirm(false);
                  setMovieToDelete(null);
                });
              }}
            >
              Eliminar
            </button>

            <button
              className="bg-gray-400 text-white px-4 py-2 rounded w-full"
              onClick={() => {
                setShowMovieConfirm(false);
                setMovieToDelete(null);
              }}
            >
              Cancelar
            </button>
          </div>
        </div>
      </div>
    )}
  </>
)}


        {/* ===== ROOMS ===== */}
{section === "ROOMS" && (
  <>
    <h2 className="text-2xl font-bold mb-4">Salas</h2>

    {currentUser.role === "ADMIN" && (
      <form
        className="bg-white p-4 rounded shadow mb-6"
        onSubmit={e => {
          e.preventDefault();

          fetch("http://localhost:8080/api/rooms", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              name: newRoomName,
              totalSeats: newRoomSeats
            })
          }).then(() => {
            setNewRoomName("");
            setNewRoomSeats("");

            fetch("http://localhost:8080/api/rooms")
              .then(r => r.json())
              .then(setRooms);
          });
        }}
      >
        <h3 className="font-bold mb-2">Crear sala</h3>

        <input
          className="border p-2 w-full mb-2"
          placeholder="Nombre"
          value={newRoomName}
          onChange={e => setNewRoomName(e.target.value)}
        />

        <input
          className="border p-2 w-full mb-2"
          type="number"
          placeholder="Asientos"
          value={newRoomSeats}
          onChange={e => setNewRoomSeats(e.target.value)}
        />

        <button className="bg-blue-600 text-white px-4 py-2 rounded">
          Crear sala
        </button>
      </form>
    )}

    <ul>
      {rooms.map(room => (
        <li
          key={room.id}
          className="bg-white p-3 mb-2 rounded shadow flex justify-between items-center"
        >
          <span>
            {room.name} — {room.totalSeats} asientos
          </span>

          {currentUser.role === "ADMIN" && (
            <button
              className="bg-red-500 text-white px-3 py-1 rounded"
              onClick={() => {
                setRoomToDelete(room);
                setShowRoomConfirm(true);
              }}
            >
              Eliminar
            </button>
          )}
        </li>
      ))}
    </ul>

    {/* ===== MODAL CONFIRMACIÓN ELIMINAR SALA ===== */}
    {showRoomConfirm && roomToDelete && (
      <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg shadow-lg p-6 w-96 text-center">
          <h3 className="text-xl font-bold mb-4">
            Eliminar sala
          </h3>

          <p className="mb-6">
            ¿Seguro que quieres eliminar la sala<br />
            <span className="font-semibold">
              {roomToDelete.name}
            </span>?
            <br />
            <span className="text-sm text-red-600">
              (Se eliminarán también sus sesiones)
            </span>
          </p>

          <div className="flex gap-4">
            <button
              className="bg-red-600 text-white px-4 py-2 rounded w-full"
              onClick={() => {
                fetch(
                  `http://localhost:8080/api/rooms/${roomToDelete.id}`,
                  { method: "DELETE" }
                ).then(() => {
                  setRooms(
                    rooms.filter(r => r.id !== roomToDelete.id)
                  );

                  fetch("http://localhost:8080/api/sessions")
                    .then(r => r.json())
                    .then(setSessions);

                  setShowRoomConfirm(false);
                  setRoomToDelete(null);
                });
              }}
            >
              Eliminar
            </button>

            <button
              className="bg-gray-400 text-white px-4 py-2 rounded w-full"
              onClick={() => {
                setShowRoomConfirm(false);
                setRoomToDelete(null);
              }}
            >
              Cancelar
            </button>
          </div>
        </div>
      </div>
    )}
  </>
)}


        {/* ===== SESSIONS ===== */}
{section === "SESSIONS" && (
  <>
    <h2 className="text-2xl font-bold mb-4">Sesiones</h2>

    {currentUser.role === "ADMIN" && (
      <form
        onSubmit={createSession}
        className="bg-white p-4 rounded shadow mb-6"
      >
        <select
          className="border p-2 w-full mb-2"
          value={movieId}
          onChange={e => setMovieId(e.target.value)}
          required
        >
          <option value="" disabled>Película</option>
          {movies.map(m => (
            <option key={m.id} value={m.id}>
              {m.title}
            </option>
          ))}
        </select>

        <select
          className="border p-2 w-full mb-2"
          value={roomId}
          onChange={e => setRoomId(e.target.value)}
          required
        >
          <option value="" disabled>Sala</option>
          {rooms.map(r => (
            <option key={r.id} value={r.id}>
              {r.name}
            </option>
          ))}
        </select>

        <input
          className="border p-2 w-full mb-2"
          type="datetime-local"
          value={dateTime}
          onChange={e => setDateTime(e.target.value)}
          required
        />

        <button className="bg-blue-600 text-white px-4 py-2 rounded">
          Crear sesión
        </button>
      </form>
    )}

    <ul>
      {sessions.map(s => (
        <li
          key={s.id}
          className="bg-white p-3 mb-2 rounded shadow flex justify-between items-center"
        >
          <span>
            {s.movie.title} — {s.room.name} —{" "}
            {formatDateTime(s.dateTime)}
          </span>

          {currentUser.role === "ADMIN" && (
            <button
              className="bg-red-500 text-white px-3 py-1 rounded"
              onClick={() => {
                setSessionToDelete(s);
                setShowSessionConfirm(true);
              }}
            >
              Eliminar
            </button>
          )}
        </li>
      ))}
    </ul>

    {/* ===== MODAL CONFIRMACIÓN ELIMINAR SESIÓN ===== */}
    {showSessionConfirm && sessionToDelete && (
      <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg shadow-lg p-6 w-96 text-center">
          <h3 className="text-xl font-bold mb-4">
            Eliminar sesión
          </h3>

          <p className="mb-6">
            ¿Seguro que quieres eliminar la sesión de
            <br />
            <span className="font-semibold">
              {sessionToDelete.movie.title}
            </span>
            <br />
            en la sala{" "}
            <span className="font-semibold">
              {sessionToDelete.room.name}
            </span>
            ?
          </p>

          <div className="flex gap-4">
            <button
              className="bg-red-600 text-white px-4 py-2 rounded w-full"
              onClick={() => {
                fetch(
                  `http://localhost:8080/api/sessions/${sessionToDelete.id}`,
                  { method: "DELETE" }
                ).then(() => {
                  setSessions(
                    sessions.filter(s => s.id !== sessionToDelete.id)
                  );
                  setShowSessionConfirm(false);
                  setSessionToDelete(null);
                });
              }}
            >
              Eliminar
            </button>

            <button
              className="bg-gray-400 text-white px-4 py-2 rounded w-full"
              onClick={() => {
                setShowSessionConfirm(false);
                setSessionToDelete(null);
              }}
            >
              Cancelar
            </button>
          </div>
        </div>
      </div>
    )}
  </>
)}


{/* ===== TICKETS ===== */}
{section === "TICKETS" && (
  <>
    <h2 className="text-2xl font-bold mb-4">Gestionar entradas</h2>

    <select
      className="border p-2 mb-4"
      required
      value={selectedSession?.id || ""}
      onChange={e => {
        const session = sessions.find(
          s => s.id === Number(e.target.value)
        );
        if (!session) return;

        setSelectedSession(session);

        fetch(`http://localhost:8080/api/tickets/session/${session.id}`)
          .then(r => r.json())
          .then(data => setTakenSeats(data.map(t => t.seatNumber)));
      }}
    >
      <option value="" disabled>
        Selecciona sesión
      </option>

      {[...sessions]
        .sort((a, b) => new Date(a.dateTime) - new Date(b.dateTime))
        .map(s => (
          <option key={s.id} value={s.id}>
            {s.movie.title} — {s.room.name} — {formatDateTime(s.dateTime)}
          </option>
        ))}
    </select>

    {selectedSession && (
      <div className="max-w-5xl mx-auto mt-4">

        {/* ===== INFO SALA ===== */}
        <div className="text-center mb-3 text-lg font-semibold flex justify-center items-center gap-6">
          <span>
            {selectedSession.room.name} · {selectedSession.room.totalSeats} asientos
          </span>
          <span className="text-red-600">
            Ocupados: {takenSeats.length}
          </span>
          <span className="text-green-600">
            Libres: {selectedSession.room.totalSeats - takenSeats.length}
          </span>
        </div>

        {/* ===== INFO PELÍCULA (A LA IZQUIERDA) ===== */}
        <div className="mb-6">
          <div className="flex gap-4 items-start">
            {selectedSession.movie.imageUrl && (
              <img
                src={selectedSession.movie.imageUrl}
                alt={selectedSession.movie.title}
                className="w-40 h-56 object-cover rounded shadow"
              />
            )}

            <div>
              <h3 className="text-2xl font-bold mb-2">
                {selectedSession.movie.title}
              </h3>
              <p className="text-gray-600">
                Duración: {selectedSession.movie.duration} min
              </p>
            </div>
          </div>
        </div>

        {/* ===== PANTALLA (CENTRADA) ===== */}
        <div className="bg-gray-300 text-center py-1 mb-5 rounded font-bold w-2/3 mx-auto">
          PANTALLA
        </div>

        {/* ===== SALA (CENTRADA COMO ANTES) ===== */}
        <div className="flex flex-col gap-3 items-center">
          {Array.from({
            length: Math.ceil(selectedSession.room.totalSeats / 12)
          }).map((_, rowIndex) => (
            <div key={rowIndex} className="flex gap-8">

              {/* LADO IZQUIERDO */}
              <div className="flex gap-2">
                {Array.from({ length: 6 }).map((_, seatIndex) => {
                  const seatNumber = rowIndex * 12 + seatIndex + 1;
                  if (seatNumber > selectedSession.room.totalSeats) return null;

                  const isTaken = takenSeats.includes(seatNumber);

                  return (
                    <div
                      key={seatNumber}
                      className={`w-11 h-11 rounded flex items-center justify-center text-sm font-bold ${
                        isTaken
                          ? "bg-red-500 text-white"
                          : "bg-green-600 text-white"
                      }`}
                    >
                      {seatNumber}
                    </div>
                  );
                })}
              </div>

              <div className="w-8"></div>

              {/* LADO DERECHO */}
              <div className="flex gap-2">
                {Array.from({ length: 6 }).map((_, seatIndex) => {
                  const seatNumber = rowIndex * 12 + seatIndex + 7;
                  if (seatNumber > selectedSession.room.totalSeats) return null;

                  const isTaken = takenSeats.includes(seatNumber);

                  return (
                    <div
                      key={seatNumber}
                      className={`w-11 h-11 rounded flex items-center justify-center text-sm font-bold ${
                        isTaken
                          ? "bg-red-500 text-white"
                          : "bg-green-600 text-white"
                      }`}
                    >
                      {seatNumber}
                    </div>
                  );
                })}
              </div>

            </div>
          ))}
        </div>

        {/* ===== MODAL ASIENTOS ===== */}
        {showSeatModal && (
  <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
    <div className="bg-white rounded-lg shadow-lg p-6 w-96 text-center">
      <h3 className="text-xl font-bold mb-4">Información del asiento</h3>

      <p className="mb-4 text-lg">{seatInfo}</p>

      {/* ===== SI ESTÁ OCUPADO ===== */}
      {seatInfo.includes("ocupado") && (
        <>
          {/* INPUT NUEVO ASIENTO */}
          <input
            type="number"
            min={1}
            max={selectedSession.room.totalSeats}
            placeholder={`Nuevo asiento (1 - ${selectedSession.room.totalSeats})`}
            className="border p-2 w-full mb-3"
            value={newSeat}
            onChange={e => setNewSeat(e.target.value)}
          />

          {/* BOTONES PRINCIPALES */}
          {!confirmAction && (
            <>
              <button
                className="bg-yellow-500 text-white px-4 py-2 rounded w-full mb-2"
                onClick={() => {
                  const seatNum = Number(newSeat);

                  if (!seatNum) {
                    alert("Introduce un número válido");
                    return;
                  }

                  if (
                    seatNum < 1 ||
                    seatNum > selectedSession.room.totalSeats
                  ) {
                    alert("Asiento fuera del rango de la sala");
                    return;
                  }

                  if (takenSeats.includes(seatNum)) {
                    alert("Ese asiento ya está ocupado");
                    return;
                  }

                  setConfirmAction("CHANGE");
                }}
              >
                Cambiar asiento
              </button>

              <button
                className="bg-red-600 text-white px-4 py-2 rounded w-full"
                onClick={() => setConfirmAction("DELETE")}
              >
                Liberar asiento
              </button>
            </>
          )}

          {/* ===== CONFIRMACIÓN ===== */}
          {confirmAction && (
            <div className="mt-4 bg-gray-100 p-4 rounded">
              <p className="mb-4 font-semibold">
                {confirmAction === "CHANGE"
                  ? `¿Confirmar cambio al asiento ${newSeat}?`
                  : `¿Confirmar liberación del asiento ${selectedSeat}?`}
              </p>

              <div className="flex gap-3">
                <button
                  className="bg-green-600 text-white px-4 py-2 rounded w-full"
                  onClick={() => {
                    if (confirmAction === "CHANGE") {
                      fetch(
                        `http://localhost:8080/api/tickets/session/${selectedSession.id}/seat/${selectedSeat}/change/${newSeat}`,
                        { method: "PUT" }
                      ).then(() => {
                        alert("Asiento cambiado");
                      });
                    }

                    if (confirmAction === "DELETE") {
                      fetch(
                        `http://localhost:8080/api/tickets/session/${selectedSession.id}/seat/${selectedSeat}`,
                        { method: "DELETE" }
                      ).then(() => {
                        alert("Asiento liberado");
                      });
                    }

                    setConfirmAction(null);
                    setShowSeatModal(false);
                    setNewSeat("");

                    fetch(
                      `http://localhost:8080/api/tickets/session/${selectedSession.id}`
                    )
                      .then(r => r.json())
                      .then(data =>
                        setTakenSeats(data.map(t => t.seatNumber))
                      );
                  }}
                >
                  Confirmar
                </button>

                <button
                  className="bg-gray-400 text-white px-4 py-2 rounded w-full"
                  onClick={() => setConfirmAction(null)}
                >
                  Cancelar
                </button>
              </div>
            </div>
          )}
        </>
      )}

      {/* CERRAR */}
      <button
        className="mt-4 text-gray-600 underline"
        onClick={() => {
          setConfirmAction(null);
          setShowSeatModal(false);
        }}
      >
        Cerrar
      </button>
    </div>
  </div>
)}


      </div>
    )}
  </>
)}


{/* ===== STAR WARS ===== */}
{section === "STARWARS" && (
  <>
    <h2 className="text-2xl font-bold mb-4">Campaña Star Wars</h2>

    <button
      className="bg-yellow-500 text-black px-4 py-2 rounded mb-4"
      onClick={() => {
        setLoadingSw(true);
        fetch("http://localhost:8080/api/swapi/movies")
  .then(r => r.json())
  .then(data => {
    console.log("SWAPI DATA PARSED:", data);
    setSwMovies(Array.isArray(data) ? data : []);
    setLoadingSw(false);
  })
  .catch(err => {
    console.error(err);
    setSwMovies([]);
    setLoadingSw(false);
  });

      }}
    >
      Cargar películas de Star Wars
    </button>

    {loadingSw && <p>Cargando películas...</p>}

    <ul>
      {Array.isArray(swMovies) && swMovies.map(movie => (
        <li
          key={movie.title}
          className="bg-white p-3 mb-2 rounded shadow flex justify-between items-center"
        >
          <div className="flex items-center gap-4">
  <img
    src={`https://starwars-visualguide.com/assets/img/films/${movie.episode_id}.jpg`}
    alt={movie.title}
    className="w-20 h-28 object-cover rounded"
  />

  <div>
    <p className="font-bold">{movie.title}</p>
    <p className="text-sm text-gray-600">
      Estreno: {movie.release_date}
    </p>
  </div>
</div>

          <button
  disabled={movieExists(movie.title)}
  className={`px-3 py-1 rounded text-white ${
    movieExists(movie.title)
      ? "bg-gray-400 cursor-not-allowed"
      : "bg-blue-600 hover:bg-blue-700"
  }`}
  onClick={() => {
    fetch("http://localhost:8080/api/swapi/import", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(movie)
    })
      .then(res => {
        if (!res.ok) throw new Error("La película ya existe");
      })
      .then(() => {
        alert("Película importada a CineVerse");
        fetch("http://localhost:8080/api/movies")
          .then(r => r.json())
          .then(setMovies);
      })
      .catch(err => alert(err.message));
  }}
>
  {movieExists(movie.title) ? "Ya importada" : "Importar"}
</button>
        </li>
      ))}
    </ul>
  </>
)}

      </main>
    </div>
  );
}

export default App;
