import React, { useState } from "react";

export default function ChatWidget() {
  const [showChat, setShowChat] = useState(false);

  return (
    <>
      {/* CHAT FLOAT BUTTON */}
      <button
        onClick={() => setShowChat(true)}
        className="fixed bottom-6 right-6 bg-blue-600 hover:bg-blue-700 text-white
                   w-14 h-14 rounded-full shadow-lg flex items-center justify-center
                   text-2xl z-50"
        title="Chat con clientes"
      >
        üí¨
      </button>

      {/* CHAT WINDOW */}
      {showChat && (
        <div className="fixed bottom-24 right-6 w-80 h-96 bg-white rounded-lg shadow-xl
                        flex flex-col z-50">
          {/* HEADER */}
          <div className="bg-blue-600 text-white p-3 rounded-t-lg
                          flex justify-between items-center">
            <span className="font-semibold">Chat CineVerse</span>
            <button
              onClick={() => setShowChat(false)}
              className="text-white font-bold"
            >
              ‚úï
            </button>
          </div>

          {/* MENSAJES (VAC√çO DE MOMENTO) */}
          <div className="flex-1 p-3 overflow-y-auto text-sm text-gray-500">
            <p>Chat en construcci√≥n‚Ä¶</p>
          </div>

          {/* INPUT */}
          <div className="p-2 border-t">
            <input
              type="text"
              placeholder="Escribe un mensaje‚Ä¶"
              className="w-full border rounded p-2 text-sm"
              disabled
            />
          </div>
        </div>
      )}
    </>
  );
}
