import React, { useEffect, useRef, useState } from "react";
import { connectChat, sendMessage } from "./ChatService";

export default function ChatBox({ user }) {
  const messagesEndRef = useRef(null);
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [otherPersonName, setOtherPersonName] = useState("");

  const STORAGE_KEY = user?.email
    ? `cineverse_chat_messages_${user.email}`
    : null;

  const isAdmin = user?.role && user.role !== "USER";

  // 游릭 Cargar mensajes SOLO cuando ya existe user
  useEffect(() => {
    if (!STORAGE_KEY) return;

    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved) {
      setMessages(JSON.parse(saved));
    }
  }, [STORAGE_KEY]);

  // 游릭 Guardar mensajes
  useEffect(() => {
    if (!STORAGE_KEY) return;
    localStorage.setItem(STORAGE_KEY, JSON.stringify(messages));
  }, [messages, STORAGE_KEY]);

  // 游릭 WebSocket (UNA SOLA VEZ)
  useEffect(() => {
    connectChat(msg => {
      setMessages(prev => {
        const exists = prev.some(
          m => m.sender === msg.sender && m.content === msg.content
        );
        if (exists) return prev;
        return [...prev, msg];
      });

      if (!otherPersonName && msg.sender !== user.email) {
        setOtherPersonName(msg.sender);
      }
    });
  }, []); // 丘멆잺 SOLO UNA VEZ

  // 游릭 Scroll autom치tico
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSend = () => {
    if (text.trim()) {
      sendMessage(user.email, text);
      setText("");
    }
  };

  const handleKeyPress = e => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="flex flex-col h-full p-3">
      <div className="font-bold mb-2">
        Chat de soporte
        {isAdmin && otherPersonName && ` - ${otherPersonName}`}
      </div>

      <div className="h-48 overflow-y-auto mb-2 text-sm">
        {messages.map((m, i) => {
          const isMe = m.sender === user.email;

          return (
            <div
              key={i}
              className={`mb-2 flex ${isMe ? "justify-end" : "justify-start"}`}
            >
              <div
                className={`px-3 py-2 rounded-lg text-sm max-w-[70%]
                  ${isMe ? "bg-blue-600 text-white" : "bg-gray-300 text-gray-900"}`}
              >
                <div className="font-semibold text-xs mb-1">
                  {isMe ? "T칰" : m.sender}
                </div>
                {m.content}
              </div>
            </div>
          );
        })}
        <div ref={messagesEndRef} />
      </div>

      <input
        className="border p-2 w-full mb-1 rounded"
        value={text}
        onChange={e => setText(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="Escribe un mensaje..."
      />
      <button
        className="bg-blue-600 text-white w-full"
        onClick={handleSend}
      >
        Enviar
      </button>
    </div>
  );
}
