import { useEffect, useState } from "react";

function App() {
  // ===== AUTH =====
  const [currentUser, setCurrentUser] = useState(null);
  const [isRegister, setIsRegister] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // ===== UI =====
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [section, setSection] = useState("MOVIES");

  // ===== DATA =====
  const [movies, setMovies] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [sessions, setSessions] = useState([]);

  // ===== SESSION FORM =====
  const [movieId, setMovieId] = useState("");
  const [roomId, setRoomId] = useState("");
  const [dateTime, setDateTime] = useState("");

  // ===== LOGIN =====
  const login = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/api/users/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    })
      .then(res => {
        if (!res.ok) throw new Error();
        return res.json();
      })
      .then(user => {
        setCurrentUser(user);
        setError("");
      })
      .catch(() => setError("Credenciales incorrectas"));
  };

  // ===== REGISTER =====
  const register = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/api/users/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    })
      .then(() => {
        setIsRegister(false);
        setError("Usuario creado, ahora inicia sesi√≥n");
      });
  };

  // ===== LOGOUT =====
  const logout = () => {
    setCurrentUser(null);
    setSidebarOpen(false);
    setUsername("");
    setPassword("");
  };

  // ===== LOAD DATA =====
  useEffect(() => {
    if (currentUser) {
      fetch("http://localhost:8080/api/movies").then(r => r.json()).then(setMovies);
      fetch("http://localhost:8080/api/rooms").then(r => r.json()).then(setRooms);
      fetch("http://localhost:8080/api/sessions").then(r => r.json()).then(setSessions);
    }
  }, [currentUser]);

  // ===== CREATE SESSION =====
  const createSession = (e) => {
    e.preventDefault();

    fetch(`http://localhost:8080/api/sessions?movieId=${movieId}&roomId=${roomId}&dateTime=${dateTime}`, {
      method: "POST"
    }).then(() => {
      setMovieId("");
      setRoomId("");
      setDateTime("");
      fetch("http://localhost:8080/api/sessions")
        .then(r => r.json())
        .then(setSessions);
    });
  };

  // ===== LOGIN / REGISTER UI =====
  if (!currentUser) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <form
          onSubmit={isRegister ? register : login}
          className="bg-white p-6 rounded shadow w-80"
        >
          <h2 className="text-xl font-bold mb-4">
            {isRegister ? "Registro" : "Login"}
          </h2>

          {error && <p className="text-red-500 mb-2">{error}</p>}

          <input
            className="border p-2 w-full mb-2"
            placeholder="Usuario"
            value={username}
            onChange={e => setUsername(e.target.value)}
            required
          />

          <input
            className="border p-2 w-full mb-4"
            type="password"
            placeholder="Contrase√±a"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
          />

          <button className="bg-blue-600 text-white w-full p-2 rounded mb-2">
            {isRegister ? "Crear cuenta" : "Entrar"}
          </button>

          <p
            className="text-sm text-center text-blue-600 cursor-pointer"
            onClick={() => {
              setIsRegister(!isRegister);
              setError("");
            }}
          >
            {isRegister
              ? "¬øYa tienes cuenta? Inicia sesi√≥n"
              : "¬øNo tienes cuenta? Reg√≠strate"}
          </p>
        </form>
      </div>
    );
  }

  // ===== APP =====
  return (
    <div className="min-h-screen bg-gray-100">
      {/* HEADER */}
      <header className="bg-gray-900 text-white p-4 flex justify-between items-center">
        <button onClick={() => setSidebarOpen(!sidebarOpen)}>‚ò∞</button>
        <span>{currentUser.username} ({currentUser.role})</span>
      </header>

      {/* SIDEBAR */}
      {sidebarOpen && (
        <aside className="fixed top-0 left-0 h-full w-64 bg-gray-800 text-white p-4 z-50">
          <h2 className="text-xl font-bold mb-6">CineVerse</h2>

          <div
            className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => {
              setSection("MOVIES");
              setSidebarOpen(false);
            }}
          >
            Pel√≠culas
          </div>

          <div
            className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => {
              setSection("SESSIONS");
              setSidebarOpen(false);
            }}
          >
            Sesiones
          </div>

          <div
            className="p-2 bg-red-600 mt-6 cursor-pointer"
            onClick={logout}
          >
            Cerrar sesi√≥n
          </div>
        </aside>
      )}

      {/* CONTENT */}
      <main className="p-6">
        {/* ===== MOVIES ===== */}
        {section === "MOVIES" && (
          <>
            <h2 className="text-2xl font-bold mb-4">Pel√≠culas</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {movies.map(movie => (
                <div key={movie.id} className="bg-white rounded shadow overflow-hidden">
                  {movie.imageUrl && (
                    <img
                      src={movie.imageUrl}
                      alt={movie.title}
                      className="w-full h-48 object-cover"
                    />
                  )}
                  <div className="p-4">
                    <h3 className="text-xl font-bold">{movie.title}</h3>
                    <p className="text-sm text-gray-600">{movie.duration} min</p>
                    <p className="mt-2">{movie.description}</p>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}

        {/* ===== SESSIONS ===== */}
        {section === "SESSIONS" && (
          <>
            <h2 className="text-2xl font-bold mb-4">Sesiones</h2>

            {currentUser.role === "ADMIN" && (
              <form onSubmit={createSession} className="bg-white p-4 rounded shadow mb-6">
                <select className="border p-2 w-full mb-2"
                  value={movieId} onChange={e => setMovieId(e.target.value)} required>
                  <option value="">Pel√≠cula</option>
                  {movies.map(m => (
                    <option key={m.id} value={m.id}>{m.title}</option>
                  ))}
                </select>

                <select className="border p-2 w-full mb-2"
                  value={roomId} onChange={e => setRoomId(e.target.value)} required>
                  <option value="">Sala</option>
                  {rooms.map(r => (
                    <option key={r.id} value={r.id}>{r.name}</option>
                  ))}
                </select>

                <input
                  className="border p-2 w-full mb-2"
                  type="datetime-local"
                  value={dateTime}
                  onChange={e => setDateTime(e.target.value)}
                  required
                />

                <button className="bg-blue-600 text-white px-4 py-2 rounded">
                  Crear sesi√≥n
                </button>
              </form>
            )}

            <ul>
              {sessions.map(s => (
                <li key={s.id} className="bg-white p-3 mb-2 rounded shadow">
                  üé¨ {s.movie.title} ‚Äî üè¢ {s.room.name} ‚Äî üìÖ {s.dateTime}
                </li>
              ))}
            </ul>
          </>
        )}
      </main>
    </div>
  );
}

export default App;
