import { useState } from "react";

function App() {
  // üîê Sesi√≥n
  const [currentUser, setCurrentUser] = useState(null);

  // UI
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [section, setSection] = useState("MOVIES");

  // Login
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // LOGIN REAL
  const handleLogin = (e) => {
    e.preventDefault();

    fetch("http://localhost:8080/api/users/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    })
      .then(res => {
        if (!res.ok) throw new Error();
        return res.json();
      })
      .then(user => {
        setCurrentUser(user);
        setError("");
      })
      .catch(() => setError("Usuario o contrase√±a incorrectos"));
  };

  // LOGOUT
  const logout = () => {
    setCurrentUser(null);
    setUsername("");
    setPassword("");
    setSidebarOpen(false);
  };

  // ===== LOGIN =====
  if (!currentUser) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <form onSubmit={handleLogin} className="bg-white p-6 rounded shadow w-80">
          <h2 className="text-xl font-bold mb-4">Login CineVerse</h2>

          {error && <p className="text-red-500 mb-2">{error}</p>}

          <input
            className="border p-2 w-full mb-2"
            placeholder="Usuario"
            value={username}
            onChange={e => setUsername(e.target.value)}
            required
          />

          <input
            className="border p-2 w-full mb-4"
            type="password"
            placeholder="Contrase√±a"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
          />

          <button className="bg-blue-600 text-white w-full p-2 rounded">
            Entrar
          </button>
        </form>
      </div>
    );
  }

  // ===== APP =====
  return (
    <div className="min-h-screen bg-gray-100">

      {/* HEADER */}
      <header className="bg-gray-900 text-white p-4 flex justify-between items-center">
        <button onClick={() => setSidebarOpen(!sidebarOpen)}>‚ò∞</button>
        <span>{currentUser.username} ({currentUser.role})</span>
      </header>

      {/* SIDEBAR */}
      {sidebarOpen && (
        <aside className="fixed top-0 left-0 h-full w-64 bg-gray-800 text-white p-4">
          <h2 className="text-xl font-bold mb-6">CineVerse</h2>

          <div className="space-y-2">
            <div onClick={() => setSection("MOVIES")} className="cursor-pointer hover:bg-gray-700 p-2 rounded">
              Pel√≠culas
            </div>
            <div onClick={() => setSection("ROOMS")} className="cursor-pointer hover:bg-gray-700 p-2 rounded">
              Salas
            </div>
            <div onClick={logout} className="cursor-pointer bg-red-600 p-2 rounded mt-6">
              Cerrar sesi√≥n
            </div>
          </div>
        </aside>
      )}

      {/* CONTENIDO */}
      <main className="p-6">
        <h2 className="text-2xl font-bold">
          {section === "MOVIES" ? "Gesti√≥n de Pel√≠culas" : "Gesti√≥n de Salas"}
        </h2>
        <p className="mt-2 text-gray-600">
          (Aqu√≠ ya conectar√≠as lo que hicimos antes)
        </p>
      </main>

    </div>
  );
}

export default App;
