import React, { useEffect, useState } from "react";
import { fetchData } from "../../utils/api";

export default function UsersSection() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const loadUsers = async () => {
    setLoading(true);
    setErrorMessage("");
    try {
      const data = await fetchData("http://localhost:8080/api/users");
      setUsers(Array.isArray(data) ? data : []);
    } catch (e) {
      console.error(e);
      setUsers([]);
      setErrorMessage(e.message || "Error al cargar usuarios");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { loadUsers(); }, []);

  // simple filtered list (no actions)
  const filtered = users;

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">Gesti√≥n de usuarios</h2>

      <div className="mb-4 flex items-center gap-2">
        <button onClick={loadUsers} className="bg-gray-200 hover:bg-gray-300 px-3 py-2 rounded transition-colors">Recargar</button>
      </div>

      {loading ? (
        <p>Cargando usuarios...</p>
      ) : (
        <ul className="space-y-2">
          {filtered.map(u => (
            <li key={u.id} className="bg-white p-3 rounded shadow flex flex-col">
              <div className="font-semibold">{u.username} <span className="text-sm text-gray-500">({u.email})</span></div>
              <div className="text-sm text-gray-600">{u.fullName}</div>
              <div className="text-sm text-gray-500 mt-2">Rol: {u.role}</div>
            </li>
          ))}

          {filtered.length === 0 && (
            <li className="text-gray-500">No se encontraron usuarios</li>
          )}
        </ul>
      )}

      {errorMessage && <p className="text-red-500 mt-4">{errorMessage}</p>}
    </div>
  );
}
