import { useEffect, useState } from "react";

function App() {
const [showChat, setShowChat] = useState(false);

  // ===== AUTH =====
  const [currentUser, setCurrentUser] = useState(null);
  const [isRegister, setIsRegister] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // ===== UI =====
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [section, setSection] = useState("MOVIES");

  // ===== DATA =====
  const [movies, setMovies] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [sessions, setSessions] = useState([]);

  // ===== SESSION FORM =====
  const [movieId, setMovieId] = useState("");
  const [roomId, setRoomId] = useState("");
  const [dateTime, setDateTime] = useState("");

const [showSessionConfirm, setShowSessionConfirm] = useState(false);
const [sessionToDelete, setSessionToDelete] = useState(null);


  // ===== MOVIE FORM =====
  const [newMovieTitle, setNewMovieTitle] = useState("");
  const [newMovieDescription, setNewMovieDescription] = useState("");
  const [newMovieDuration, setNewMovieDuration] = useState("");
  const [newMovieImageUrl, setNewMovieImageUrl] = useState("");

const [showMovieConfirm, setShowMovieConfirm] = useState(false);
const [movieToDelete, setMovieToDelete] = useState(null);

  // ===== ROOM FORM =====
  const [newRoomName, setNewRoomName] = useState("");
  const [newRoomSeats, setNewRoomSeats] = useState("");

const [showRoomConfirm, setShowRoomConfirm] = useState(false);
const [roomToDelete, setRoomToDelete] = useState(null);


  // ===== TICKETS =====
  const [selectedSession, setSelectedSession] = useState(null);
  const [takenSeats, setTakenSeats] = useState([]);
const [showSeatModal, setShowSeatModal] = useState(false);

// ===== SEAT INFO =====
const [selectedSeat, setSelectedSeat] = useState(null);
const [seatInfo, setSeatInfo] = useState("");
const [newSeat, setNewSeat] = useState("");

const [confirmAction, setConfirmAction] = useState(null); 

// ===== STAR WARS =====
const [swMovies, setSwMovies] = useState([]);
const [loadingSw, setLoadingSw] = useState(false);

const movieExists = (title) => {
  return movies.some(m => m.title.toLowerCase() === title.toLowerCase());
};

  // ===== HELPERS =====
  const formatDateTime = (dateTimeString) => {
    const date = new Date(dateTimeString);
    return date.toLocaleString("es-ES", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
      hour: "2-digit",
      minute: "2-digit"
    });
  };

  // ===== LOGIN =====
  const login = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/api/users/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    })
      .then(res => {
        if (!res.ok) throw new Error();
        return res.json();
      })
      .then(user => {
        setCurrentUser(user);
        setError("");
      })
      .catch(() => setError("Credenciales incorrectas"));
  };

  // ===== REGISTER =====
  const register = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/api/users/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    }).then(() => {
      setIsRegister(false);
      setError("Usuario creado, ahora inicia sesiÃ³n");
    });
  };

  // ===== LOGOUT =====
  const logout = () => {
    setCurrentUser(null);
    setSidebarOpen(false);
    setUsername("");
    setPassword("");
  };

useEffect(() => {
  if (!selectedSession) return;

  const interval = setInterval(() => {
    fetch(`http://localhost:8080/api/tickets/session/${selectedSession.id}`)
      .then(r => r.json())
      .then(data => setTakenSeats(data.map(t => t.seatNumber)));
  }, 3000); // cada 3 segundos

  return () => clearInterval(interval);
}, [selectedSession]);

useEffect(() => {
  const interval = setInterval(() => {
    fetch("http://localhost:8080/api/movies")
      .then(r => r.json())
      .then(setMovies);
  }, 3000); // cada 3 segundos

  return () => clearInterval(interval);
}, []);

const refreshSessions = () => {
  fetch("http://localhost:8080/api/sessions")
    .then(r => r.json())
    .then(data => {
      setSessions(data);

      // si la sesiÃ³n seleccionada ya no existe
      if (
        selectedSession &&
        !data.find(s => s.id === selectedSession.id)
      ) {
        setSelectedSession(null);
        setTakenSeats([]);
        setShowSeatModal(false);
      }
    });
};

const refreshRooms = () => {
  fetch("http://localhost:8080/api/rooms")
    .then(r => r.json())
    .then(setRooms);

  fetch("http://localhost:8080/api/sessions")
    .then(r => r.json())
    .then(data => {
      setSessions(data);

      // si la sesiÃ³n seleccionada ya no existe â†’ limpiamos
      if (
        selectedSession &&
        !data.find(s => s.id === selectedSession.id)
      ) {
        setSelectedSession(null);
        setTakenSeats([]);
      }
    });
};

  // ===== LOAD DATA =====
  useEffect(() => {
    if (currentUser) {
      fetch("http://localhost:8080/api/movies").then(r => r.json()).then(setMovies);
      fetch("http://localhost:8080/api/rooms").then(r => r.json()).then(setRooms);
      fetch("http://localhost:8080/api/sessions").then(r => r.json()).then(setSessions);
    }
  }, [currentUser]);

  // ===== CREATE SESSION =====
  const createSession = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:8080/api/sessions?movieId=${movieId}&roomId=${roomId}&dateTime=${dateTime}`,
      { method: "POST" }
    ).then(() => {
      setMovieId("");
      setRoomId("");
      setDateTime("");
      fetch("http://localhost:8080/api/sessions")
        .then(r => r.json())
        .then(setSessions);
    });
  };

  // ===== LOGIN UI =====
  if (!currentUser) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <form onSubmit={isRegister ? register : login}
          className="bg-white p-6 rounded shadow w-80">
          <h2 className="text-xl font-bold mb-4">
            {isRegister ? "Registro" : "Login"}
          </h2>
          {error && <p className="text-red-500 mb-2">{error}</p>}
          <input className="border p-2 w-full mb-2"
            placeholder="Usuario"
            value={username}
            onChange={e => setUsername(e.target.value)} />
          <input className="border p-2 w-full mb-4"
            type="password"
            placeholder="ContraseÃ±a"
            value={password}
            onChange={e => setPassword(e.target.value)} />
          <button className="bg-blue-600 text-white w-full p-2 rounded mb-2">
            {isRegister ? "Crear cuenta" : "Entrar"}
          </button>
          <p className="text-sm text-center text-blue-600 cursor-pointer"
            onClick={() => { setIsRegister(!isRegister); setError(""); }}>
            {isRegister ? "Â¿Ya tienes cuenta?" : "Â¿No tienes cuenta?"}
          </p>
        </form>
      </div>
    );
  }

  // ===== APP =====
  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-gray-900 text-white p-4 flex justify-between items-center fixed top-0 left-0 w-full z-40">
        <button onClick={() => setSidebarOpen(!sidebarOpen)}>â˜°</button>
        <span>{currentUser.username} ({currentUser.role})</span>
      </header>

      {sidebarOpen && (
        <aside className="fixed top-0 left-0 h-full w-64 bg-gray-800 text-white p-4 z-50">
          <h2 className="text-xl font-bold mb-6">CineVerse</h2>

          <div className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => { setSection("MOVIES"); setSidebarOpen(false); }}>
            PelÃ­culas
          </div>

          <div className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => { setSection("ROOMS"); setSidebarOpen(false); }}>
            Salas
          </div>

          <div className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => { setSection("SESSIONS"); setSidebarOpen(false); }}>
            Sesiones
          </div>

          <div className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => { setSection("TICKETS"); setSidebarOpen(false); }}>
            Entradas
          </div>

	{currentUser.role === "ADMIN" && (
  <div
    className="p-2 hover:bg-gray-700 cursor-pointer"
    onClick={() => { setSection("STARWARS"); setSidebarOpen(false); }}
  >
    Star Wars
  </div>
)}


          <div className="p-2 bg-red-600 mt-6 cursor-pointer"
            onClick={logout}>
            Cerrar sesiÃ³n
          </div>

        </aside>
      )}

      <main className="p-6 pt-24">

        {/* ===== MOVIES ===== */}
{section === "MOVIES" && (
  <>
    <h2 className="text-2xl font-bold mb-4">PelÃ­culas</h2>

    {currentUser.role === "ADMIN" && (
      <form
        className="bg-white p-4 rounded shadow mb-6"
        onSubmit={e => {
          e.preventDefault();

          fetch("http://localhost:8080/api/movies", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              title: newMovieTitle,
              description: newMovieDescription,
              duration: newMovieDuration,
              imageUrl: newMovieImageUrl
            })
          })
            .then(res => {
              if (!res.ok) {
                throw new Error("La pelÃ­cula ya existe");
              }
            })
            .then(() => {
              alert("PelÃ­cula aÃ±adida correctamente");

              setNewMovieTitle("");
              setNewMovieDescription("");
              setNewMovieDuration("");
              setNewMovieImageUrl("");

              fetch("http://localhost:8080/api/movies")
                .then(r => r.json())
                .then(setMovies);
            })
            .catch(err => alert(err.message));
        }}
      >
        <h3 className="font-bold mb-2">AÃ±adir pelÃ­cula</h3>

        <input
          className="border p-2 w-full mb-2"
          placeholder="TÃ­tulo"
          value={newMovieTitle}
          onChange={e => setNewMovieTitle(e.target.value)}
        />

        <input
          className="border p-2 w-full mb-2"
          type="number"
          placeholder="DuraciÃ³n"
          value={newMovieDuration}
          onChange={e => setNewMovieDuration(e.target.value)}
        />

        <input
          className="border p-2 w-full mb-2"
          placeholder="URL imagen"
          value={newMovieImageUrl}
          onChange={e => setNewMovieImageUrl(e.target.value)}
        />

        <textarea
          className="border p-2 w-full mb-2"
          placeholder="DescripciÃ³n"
          value={newMovieDescription}
          onChange={e => setNewMovieDescription(e.target.value)}
        />

        <button className="bg-blue-600 text-white px-4 py-2 rounded">
          AÃ±adir pelÃ­cula
        </button>
      </form>
    )}

    {/* LISTA DE PELÃCULAS */}
    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
      {movies.map(movie => (
        <div
          key={movie.id}
          className="bg-white rounded shadow overflow-hidden"
        >
          {movie.imageUrl && (
            <img
              src={movie.imageUrl}
              alt={movie.title}
              className="w-full h-48 object-cover"
            />
          )}

          <div className="p-4">
            <h3 className="text-xl font-bold">{movie.title}</h3>
            <p className="text-sm text-gray-600">
              {movie.duration} min
            </p>
            <p className="mt-2">{movie.description}</p>

            {currentUser.role === "ADMIN" && (
              <button
                className="bg-red-500 text-white px-3 py-1 rounded mt-3"
                onClick={() => {
                  setMovieToDelete(movie);
                  setShowMovieConfirm(true);
                }}
              >
                Eliminar
              </button>
            )}
          </div>
        </div>
      ))}
    </div>

    {/* ===== MODAL CONFIRMACIÃ“N ELIMINAR (UNO SOLO) ===== */}
    {showMovieConfirm && movieToDelete && (
      <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg shadow-lg p-6 w-96 text-center">
          <h3 className="text-xl font-bold mb-4">
            Eliminar pelÃ­cula
          </h3>

          <p className="mb-6">
            Â¿Seguro que quieres eliminar<br />
            <span className="font-semibold">
              {movieToDelete.title}
            </span>?
          </p>

          <div className="flex gap-4">
            <button
              className="bg-red-600 text-white px-4 py-2 rounded w-full"
              onClick={() => {
                fetch(
                  `http://localhost:8080/api/movies/${movieToDelete.id}`,
                  { method: "DELETE" }
                ).then(() => {
                  // refrescar pelÃ­culas
                  fetch("http://localhost:8080/api/movies")
                    .then(r => r.json())
                    .then(setMovies);

                  // refrescar sesiones
                  fetch("http://localhost:8080/api/sessions")
                    .then(r => r.json())
                    .then(data => {
                      setSessions(data);

                      if (
                        selectedSession &&
                        !data.find(s => s.id === selectedSession.id)
                      ) {
                        setSelectedSession(null);
                        setTakenSeats([]);
                      }
                    });

                  setShowMovieConfirm(false);
                  setMovieToDelete(null);
                });
              }}
            >
              Eliminar
            </button>

            <button
              className="bg-gray-400 text-white px-4 py-2 rounded w-full"
              onClick={() => {
                setShowMovieConfirm(false);
                setMovieToDelete(null);
              }}
            >
              Cancelar
            </button>
          </div>
        </div>
      </div>
    )}
  </>
)}


        {/* ===== ROOMS ===== */}
{section === "ROOMS" && (
  <>
    <h2 className="text-2xl font-bold mb-4">Salas</h2>

    {currentUser.role === "ADMIN" && (
      <form
        className="bg-white p-4 rounded shadow mb-6"
        onSubmit={e => {
          e.preventDefault();

          fetch("http://localhost:8080/api/rooms", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              name: newRoomName,
              totalSeats: newRoomSeats
            })
          }).then(() => {
            setNewRoomName("");
            setNewRoomSeats("");
            refreshRooms(); // ðŸ‘ˆ UNA sola llamada
          });
        }}
      >
        <h3 className="font-bold mb-2">Crear sala</h3>

        <input
          className="border p-2 w-full mb-2"
          placeholder="Nombre"
          value={newRoomName}
          onChange={e => setNewRoomName(e.target.value)}
        />

        <input
          className="border p-2 w-full mb-2"
          type="number"
          placeholder="Asientos"
          value={newRoomSeats}
          onChange={e => setNewRoomSeats(e.target.value)}
        />

        <button className="bg-blue-600 text-white px-4 py-2 rounded">
          Crear sala
        </button>
      </form>
    )}

    <ul>
      {rooms.map(room => (
        <li
          key={room.id}
          className="bg-white p-3 mb-2 rounded shadow flex justify-between items-center"
        >
          <span>
            {room.name} â€” {room.totalSeats} asientos
          </span>

          {currentUser.role === "ADMIN" && (
            <button
              className="bg-red-500 text-white px-3 py-1 rounded"
              onClick={() => {
                setRoomToDelete(room);
                setShowRoomConfirm(true);
              }}
            >
              Eliminar
            </button>
          )}
        </li>
      ))}
    </ul>

    {/* ===== MODAL CONFIRMACIÃ“N ELIMINAR SALA ===== */}
    {showRoomConfirm && roomToDelete && (
      <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg shadow-lg p-6 w-96 text-center">
          <h3 className="text-xl font-bold mb-4">
            Eliminar sala
          </h3>

          <p className="mb-6">
            Â¿Seguro que quieres eliminar la sala<br />
            <span className="font-semibold">
              {roomToDelete.name}
            </span>?
            <br />
            <span className="text-sm text-red-600">
              (Se eliminarÃ¡n tambiÃ©n sus sesiones)
            </span>
          </p>

          <div className="flex gap-4">
            <button
              className="bg-red-600 text-white px-4 py-2 rounded w-full"
              onClick={() => {
                fetch(
                  `http://localhost:8080/api/rooms/${roomToDelete.id}`,
                  { method: "DELETE" }
                ).then(() => {
                  refreshRooms();        // ðŸ‘ˆ SOLO ESTO
                  setShowRoomConfirm(false);
                  setRoomToDelete(null);
                });
              }}
            >
              Eliminar
            </button>

            <button
              className="bg-gray-400 text-white px-4 py-2 rounded w-full"
              onClick={() => {
                setShowRoomConfirm(false);
                setRoomToDelete(null);
              }}
            >
              Cancelar
            </button>
          </div>
        </div>
      </div>
    )}
  </>
)}



        {/* ===== SESSIONS ===== */}
{section === "SESSIONS" && (
  <>
    <h2 className="text-2xl font-bold mb-4">Sesiones</h2>

    {currentUser.role === "ADMIN" && (
      <form
        onSubmit={createSession}
        className="bg-white p-4 rounded shadow mb-6"
      >
        <select
          className="border p-2 w-full mb-2"
          value={movieId}
          onChange={e => setMovieId(e.target.value)}
          required
        >
          <option value="" disabled>PelÃ­cula</option>
          {movies.map(m => (
            <option key={m.id} value={m.id}>
              {m.title}
            </option>
          ))}
        </select>

        <select
          className="border p-2 w-full mb-2"
          value={roomId}
          onChange={e => setRoomId(e.target.value)}
          required
        >
          <option value="" disabled>Sala</option>
          {rooms.map(r => (
            <option key={r.id} value={r.id}>
              {r.name}
            </option>
          ))}
        </select>

        <input
          className="border p-2 w-full mb-2"
          type="datetime-local"
          value={dateTime}
