import { useEffect, useState } from "react";

function App() {
  // ===== AUTH =====
  const [currentUser, setCurrentUser] = useState(null);
  const [isRegister, setIsRegister] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // ===== UI =====
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [section, setSection] = useState("MOVIES");

  // ===== DATA =====
  const [movies, setMovies] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [sessions, setSessions] = useState([]);

  // ===== SESSION FORM =====
  const [movieId, setMovieId] = useState("");
  const [roomId, setRoomId] = useState("");
  const [dateTime, setDateTime] = useState("");

  // ===== ROOMS FORM =====
  const [newRoomName, setNewRoomName] = useState("");
  const [newRoomSeats, setNewRoomSeats] = useState("");

  // ===== TICKETS =====
  const [selectedSession, setSelectedSession] = useState(null);
  const [takenSeats, setTakenSeats] = useState([]);

  // ===== HELPERS =====
  const formatDateTime = (dt) =>
    new Date(dt).toLocaleString("es-ES", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
      hour: "2-digit",
      minute: "2-digit"
    });

  // ===== LOGIN =====
  const login = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/api/users/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    })
      .then(r => r.ok ? r.json() : Promise.reject())
      .then(u => { setCurrentUser(u); setError(""); })
      .catch(() => setError("Credenciales incorrectas"));
  };

  // ===== REGISTER =====
  const register = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/api/users/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    }).then(() => {
      setIsRegister(false);
      setError("Usuario creado, inicia sesión");
    });
  };

  // ===== LOGOUT =====
  const logout = () => {
    setCurrentUser(null);
    setSidebarOpen(false);
  };

  // ===== LOAD DATA =====
  useEffect(() => {
    if (currentUser) {
      fetch("http://localhost:8080/api/movies").then(r => r.json()).then(setMovies);
      fetch("http://localhost:8080/api/rooms").then(r => r.json()).then(setRooms);
      fetch("http://localhost:8080/api/sessions").then(r => r.json()).then(setSessions);
    }
  }, [currentUser]);

  // ===== CREATE SESSION =====
  const createSession = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:8080/api/sessions?movieId=${movieId}&roomId=${roomId}&dateTime=${dateTime}`,
      { method: "POST" }
    ).then(res => {
      if (!res.ok) {
        return res.text().then(msg => alert(msg));
      }
      fetch("http://localhost:8080/api/sessions")
        .then(r => r.json())
        .then(setSessions);
    });
  };

  // ===== LOGIN / REGISTER UI =====
  if (!currentUser) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <form onSubmit={isRegister ? register : login}
          className="bg-white p-6 rounded shadow w-80">
          <h2 className="text-xl font-bold mb-4">
            {isRegister ? "Registro" : "Login"}
          </h2>
          {error && <p className="text-red-500">{error}</p>}
          <input className="border p-2 w-full mb-2"
            placeholder="Usuario"
            value={username}
            onChange={e => setUsername(e.target.value)} />
          <input className="border p-2 w-full mb-4"
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={e => setPassword(e.target.value)} />
          <button className="bg-blue-600 text-white w-full p-2 rounded mb-2">
            {isRegister ? "Crear cuenta" : "Entrar"}
          </button>
          <p className="text-sm text-center text-blue-600 cursor-pointer"
            onClick={() => { setIsRegister(!isRegister); setError(""); }}>
            {isRegister ? "¿Ya tienes cuenta?" : "¿No tienes cuenta?"}
          </p>
        </form>
      </div>
    );
  }

  // ===== APP =====
  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-gray-900 text-white p-4 flex justify-between">
        <button onClick={() => setSidebarOpen(!sidebarOpen)}>☰</button>
        <span>{currentUser.username} ({currentUser.role})</span>
      </header>

      {sidebarOpen && (
        <aside className="fixed top-0 left-0 h-full w-64 bg-gray-800 text-white p-4">
          <div onClick={() => { setSection("MOVIES"); setSidebarOpen(false); }}
            className="p-2 hover:bg-gray-700">Películas</div>

          {currentUser.role === "ADMIN" && (
            <>
              <div onClick={() => { setSection("SESSIONS"); setSidebarOpen(false); }}
                className="p-2 hover:bg-gray-700">Sesiones</div>
              <div onClick={() => { setSection("ROOMS"); setSidebarOpen(false); }}
                className="p-2 hover:bg-gray-700">Salas</div>
            </>
          )}

          <div onClick={() => { setSection("TICKETS"); setSidebarOpen(false); }}
            className="p-2 hover:bg-gray-700">Entradas</div>

          <div onClick={logout} className="p-2 bg-red-600 mt-6">Cerrar sesión</div>
        </aside>
      )}

      <main className="p-6">

        {/* MOVIES */}
        {section === "MOVIES" && movies.map(m => (
          <div key={m.id} className="bg-white p-4 mb-2 shadow">
            <h3 className="font-bold">{m.title}</h3>
          </div>
        ))}

        {/* ROOMS */}
        {section === "ROOMS" && currentUser.role === "ADMIN" && (
          <>
            <form onSubmit={e => {
              e.preventDefault();
              fetch("http://localhost:8080/api/rooms", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ name: newRoomName, totalSeats: newRoomSeats })
              }).then(() =>
                fetch("http://localhost:8080/api/rooms")
                  .then(r => r.json())
                  .then(setRooms)
              );
            }} className="mb-4">
              <input className="border p-2 mr-2" placeholder="Sala"
                onChange={e => setNewRoomName(e.target.value)} />
              <input className="border p-2 mr-2" type="number" placeholder="Butacas"
                onChange={e => setNewRoomSeats(e.target.value)} />
              <button className="bg-blue-600 text-white p-2">Crear</button>
            </form>

            {rooms.map(r => (
              <div key={r.id} className="bg-white p-2 mb-2 flex justify-between">
                {r.name} ({r.totalSeats})
                <button className="bg-red-500 text-white px-2"
                  onClick={() => {
                    fetch(`http://localhost:8080/api/rooms/${r.id}`, { method: "DELETE" })
                      .then(() => setRooms(rooms.filter(x => x.id !== r.id)));
                  }}>
                  X
                </button>
              </div>
            ))}
          </>
        )}

        {/* SESSIONS */}
        {section === "SESSIONS" && currentUser.role === "ADMIN" && (
          <>
            <form onSubmit={createSession} className="mb-4">
              <select onChange={e => setMovieId(e.target.value)} required>
                <option>Película</option>
                {movies.map(m => <option key={m.id} value={m.id}>{m.title}</option>)}
              </select>
              <select onChange={e => setRoomId(e.target.value)} required>
                <option>Sala</option>
                {rooms.map(r => <option key={r.id} value={r.id}>{r.name}</option>)}
              </select>
              <input type="datetime-local" onChange={e => setDateTime(e.target.value)} />
              <button>Crear</button>
            </form>

            {sessions.map(s => (
              <div key={s.id} className="bg-white p-2 mb-2 flex justify-between">
                {s.movie.title} – {s.room.name} – {formatDateTime(s.dateTime)}
                <button className="bg-red-500 text-white px-2"
                  onClick={() => {
                    fetch(`http://localhost:8080/api/sessions/${s.id}`, { method: "DELETE" })
                      .then(() => setSessions(sessions.filter(x => x.id !== s.id)));
                  }}>
                  X
                </button>
              </div>
            ))}
          </>
        )}

        {/* TICKETS */}
        {section === "TICKETS" && (
          <>
            <select onChange={e => {
              const s = sessions.find(x => x.id === Number(e.target.value));
              setSelectedSession(s);
              fetch(`http://localhost:8080/api/tickets/session/${s.id}`)
                .then(r => r.json())
                .then(d => setTakenSeats(d.map(t => t.seatNumber)));
            }}>
              <option>Sesión</option>
              {sessions.map(s => (
                <option key={s.id} value={s.id}>
                  {s.movie.title} - {formatDateTime(s.dateTime)}
                </option>
              ))}
            </select>

            {selectedSession && (
              <div className="grid grid-cols-10 gap-2 mt-4">
                {Array.from(
                  { length: selectedSession.room.totalSeats },
                  (_, i) => i + 1
                ).map(seat => (
                  <button key={seat}
                    disabled={takenSeats.includes(seat)}
                    className={`p-2 text-white ${
                      takenSeats.includes(seat) ? "bg-red-500" : "bg-green-600"
                    }`}
                    onClick={() => {
                      fetch(`http://localhost:8080/api/tickets?sessionId=${selectedSession.id}&seatNumber=${seat}&username=${currentUser.username}`,
                        { method: "POST" })
                        .then(() => setTakenSeats([...takenSeats, seat]));
                    }}>
                    {seat}
                  </button>
                ))}
              </div>
            )}
          </>
        )}
      </main>
    </div>
  );
}

export default App;
