import { useEffect, useState } from "react";

function App() {
  const [movies, setMovies] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [duration, setDuration] = useState("");

  const loadMovies = () => {
    fetch("http://localhost:8080/api/movies")
      .then(res => res.json())
      .then(data => setMovies(data));
  };

  useEffect(() => {
    loadMovies();
  }, []);

  const createMovie = (e) => {
    e.preventDefault();

    fetch("http://localhost:8080/api/movies", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        title,
        description,
        duration: parseInt(duration),
        imageUrl: ""
      })
    }).then(() => {
      setTitle("");
      setDescription("");
      setDuration("");
      loadMovies();
    });
  };

  return (
    <div className="min-h-screen bg-gray-100 flex">

      {/* Sidebar */}
      <aside className="w-64 bg-gray-900 text-white p-4">
        <h1 className="text-2xl font-bold mb-6">游꿟 CineVerse</h1>
        <nav className="space-y-2">
          <div className="hover:bg-gray-700 p-2 rounded cursor-pointer">Pel칤culas</div>
          <div className="hover:bg-gray-700 p-2 rounded cursor-pointer">Salas</div>
          <div className="hover:bg-gray-700 p-2 rounded cursor-pointer">Sesiones</div>
          <div className="hover:bg-gray-700 p-2 rounded cursor-pointer">Entradas</div>
        </nav>
      </aside>

      {/* Main content */}
      <main className="flex-1 p-6">
        <h2 className="text-3xl font-semibold mb-4">Gesti칩n de Pel칤culas</h2>

        {/* Formulario */}
        <form onSubmit={createMovie} className="bg-white p-4 rounded shadow mb-6">
          <h3 className="text-xl font-bold mb-4">A침adir pel칤cula</h3>

          <input
            className="border p-2 w-full mb-2"
            placeholder="T칤tulo"
            value={title}
            onChange={e => setTitle(e.target.value)}
            required
          />

          <textarea
            className="border p-2 w-full mb-2"
            placeholder="Descripci칩n"
            value={description}
            onChange={e => setDescription(e.target.value)}
            required
          />

          <input
            className="border p-2 w-full mb-2"
            type="number"
            placeholder="Duraci칩n (min)"
            value={duration}
            onChange={e => setDuration(e.target.value)}
            required
          />

          <button className="bg-blue-600 text-white px-4 py-2 rounded">
            Guardar pel칤cula
          </button>
        </form>

        {/* Lista */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {movies.map(movie => (
            <div key={movie.id} className="bg-white p-4 rounded shadow">
              <h3 className="text-xl font-bold">{movie.title}</h3>
              <p className="text-sm text-gray-600">{movie.duration} min</p>
              <p className="text-gray-700 mt-2">{movie.description}</p>
            </div>
          ))}
        </div>
      </main>

    </div>
  );
}

export default App;
