import { useEffect, useState } from "react";

function App() {
  // ===== AUTH =====
  const [currentUser, setCurrentUser] = useState(null);
  const [isRegister, setIsRegister] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // ===== UI =====
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [section, setSection] = useState("MOVIES");

  // ===== DATA =====
  const [movies, setMovies] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [sessions, setSessions] = useState([]);

  // ===== SESSION FORM =====
  const [movieId, setMovieId] = useState("");
  const [roomId, setRoomId] = useState("");
  const [dateTime, setDateTime] = useState("");

  // ===== MOVIE FORM =====
  const [newMovieTitle, setNewMovieTitle] = useState("");
  const [newMovieDescription, setNewMovieDescription] = useState("");
  const [newMovieDuration, setNewMovieDuration] = useState("");
  const [newMovieImageUrl, setNewMovieImageUrl] = useState("");

const [showMovieConfirm, setShowMovieConfirm] = useState(false);
const [movieToDelete, setMovieToDelete] = useState(null);

  // ===== ROOM FORM =====
  const [newRoomName, setNewRoomName] = useState("");
  const [newRoomSeats, setNewRoomSeats] = useState("");

  // ===== TICKETS =====
  const [selectedSession, setSelectedSession] = useState(null);
  const [takenSeats, setTakenSeats] = useState([]);
const [showSeatModal, setShowSeatModal] = useState(false);

// ===== SEAT INFO =====
const [selectedSeat, setSelectedSeat] = useState(null);
const [seatInfo, setSeatInfo] = useState("");
const [newSeat, setNewSeat] = useState("");

const [confirmAction, setConfirmAction] = useState(null); 

// ===== STAR WARS =====
const [swMovies, setSwMovies] = useState([]);
const [loadingSw, setLoadingSw] = useState(false);

const movieExists = (title) => {
  return movies.some(m => m.title.toLowerCase() === title.toLowerCase());
};

  // ===== HELPERS =====
  const formatDateTime = (dateTimeString) => {
    const date = new Date(dateTimeString);
    return date.toLocaleString("es-ES", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
      hour: "2-digit",
      minute: "2-digit"
    });
  };

  // ===== LOGIN =====
  const login = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/api/users/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    })
      .then(res => {
        if (!res.ok) throw new Error();
        return res.json();
      })
      .then(user => {
        setCurrentUser(user);
        setError("");
      })
      .catch(() => setError("Credenciales incorrectas"));
  };

  // ===== REGISTER =====
  const register = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/api/users/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    }).then(() => {
      setIsRegister(false);
      setError("Usuario creado, ahora inicia sesión");
    });
  };

  // ===== LOGOUT =====
  const logout = () => {
    setCurrentUser(null);
    setSidebarOpen(false);
    setUsername("");
    setPassword("");
  };

  // ===== LOAD DATA =====
  useEffect(() => {
    if (currentUser) {
      fetch("http://localhost:8080/api/movies").then(r => r.json()).then(setMovies);
      fetch("http://localhost:8080/api/rooms").then(r => r.json()).then(setRooms);
      fetch("http://localhost:8080/api/sessions").then(r => r.json()).then(setSessions);
    }
  }, [currentUser]);

  // ===== CREATE SESSION =====
  const createSession = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:8080/api/sessions?movieId=${movieId}&roomId=${roomId}&dateTime=${dateTime}`,
      { method: "POST" }
    ).then(() => {
      setMovieId("");
      setRoomId("");
      setDateTime("");
      fetch("http://localhost:8080/api/sessions")
        .then(r => r.json())
        .then(setSessions);
    });
  };

  // ===== LOGIN UI =====
  if (!currentUser) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <form onSubmit={isRegister ? register : login}
          className="bg-white p-6 rounded shadow w-80">
          <h2 className="text-xl font-bold mb-4">
            {isRegister ? "Registro" : "Login"}
          </h2>
          {error && <p className="text-red-500 mb-2">{error}</p>}
          <input className="border p-2 w-full mb-2"
            placeholder="Usuario"
            value={username}
            onChange={e => setUsername(e.target.value)} />
          <input className="border p-2 w-full mb-4"
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={e => setPassword(e.target.value)} />
          <button className="bg-blue-600 text-white w-full p-2 rounded mb-2">
            {isRegister ? "Crear cuenta" : "Entrar"}
          </button>
          <p className="text-sm text-center text-blue-600 cursor-pointer"
            onClick={() => { setIsRegister(!isRegister); setError(""); }}>
            {isRegister ? "¿Ya tienes cuenta?" : "¿No tienes cuenta?"}
          </p>
        </form>
      </div>
    );
  }

  // ===== APP =====
  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-gray-900 text-white p-4 flex justify-between items-center fixed top-0 left-0 w-full z-40">
        <button onClick={() => setSidebarOpen(!sidebarOpen)}>☰</button>
        <span>{currentUser.username} ({currentUser.role})</span>
      </header>

      {sidebarOpen && (
        <aside className="fixed top-0 left-0 h-full w-64 bg-gray-800 text-white p-4 z-50">
          <h2 className="text-xl font-bold mb-6">CineVerse</h2>

          <div className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => { setSection("MOVIES"); setSidebarOpen(false); }}>
            Películas
          </div>

          <div className="p-2 hover:bg-gray-700 cursor-pointer"
