                  {currentUser.role === "ADMIN" && (
                    <button
                      className="bg-red-500 text-white px-3 py-1 rounded"
                      onClick={() => {
                        if (!window.confirm("¿Eliminar sala?")) return;
                        fetch(`http://localhost:8080/api/rooms/${room.id}`, {
                          method: "DELETE"
                        }).then(() => {
   setRooms(rooms.filter(r => r.id !== room.id));
  fetch("http://localhost:8080/api/sessions")
    .then(r => r.json())
    .then(setSessions);
});
                      }}
                    >
                      Eliminar
                    </button>
                  )}
                </li>
              ))}
            </ul>
          </>
        )}

        {/* ===== SESSIONS ===== */}
        {section === "SESSIONS" && (
          <>
            <h2 className="text-2xl font-bold mb-4">Sesiones</h2>

            {currentUser.role === "ADMIN" && (
              <form onSubmit={createSession} className="bg-white p-4 rounded shadow mb-6">
                <select className="border p-2 w-full mb-2"
                  value={movieId} onChange={e => setMovieId(e.target.value)} required>
                  <option value="">Película</option>
                  {movies.map(m => (
                    <option key={m.id} value={m.id}>{m.title}</option>
                  ))}
                </select>

                <select className="border p-2 w-full mb-2"
                  value={roomId} onChange={e => setRoomId(e.target.value)} required>
                  <option value="">Sala</option>
                  {rooms.map(r => (
                    <option key={r.id} value={r.id}>{r.name}</option>
                  ))}
                </select>

                <input className="border p-2 w-full mb-2" type="datetime-local"
                  value={dateTime} onChange={e => setDateTime(e.target.value)} required />

                <button className="bg-blue-600 text-white px-4 py-2 rounded">
                  Crear sesión
                </button>
              </form>
