                      }}
                    >
                      Eliminar
                    </button>
                  )}
                </li>
              ))}
            </ul>
          </>
        )}

{/* ===== TICKETS ===== */}
{section === "TICKETS" && (
  <>
    <h2 className="text-2xl font-bold mb-4">Gestionar entradas</h2>

    <select
      className="border p-2 mb-4"
      required
      value={selectedSession?.id || ""}
      onChange={e => {
        const session = sessions.find(s => s.id === Number(e.target.value));
        if (!session) return;

        setSelectedSession(session);

        fetch(`http://localhost:8080/api/tickets/session/${session.id}`)
          .then(r => r.json())
          .then(data => setTakenSeats(data.map(t => t.seatNumber)));
      }}
    >
      <option value="" disabled>
        Selecciona sesión
      </option>

      {sessions.map(s => (
        <option key={s.id} value={s.id}>
          {s.movie.title} - {formatDateTime(s.dateTime)}
        </option>
      ))}
    </select>

    {selectedSession && (
      <div className="max-w-5xl mx-auto mt-4">

        {/* PANTALLA */}
        <div className="bg-gray-300 text-center py-1 mb-5 rounded font-bold w-2/3 mx-auto">
          PANTALLA
        </div>

        {/* SALA */}
        <div className="flex flex-col gap-3 items-center">
          {Array.from({
            length: Math.ceil(selectedSession.room.totalSeats / 12)
          }).map((_, rowIndex) => (
            <div key={rowIndex} className="flex gap-8">

              {/* LADO IZQUIERDO (6) */}
              <div className="flex gap-2">
                {Array.from({ length: 6 }).map((_, seatIndex) => {
                  const seatNumber = rowIndex * 12 + seatIndex + 1;
                  if (seatNumber > selectedSession.room.totalSeats) return null;

                  const isTaken = takenSeats.includes(seatNumber);

                  return (
                    <div
                      key={seatNumber}
                      onClick={() => {
                        fetch(
                          `http://localhost:8080/api/tickets/session/${selectedSession.id}/seat/${seatNumber}`
                        )
                          .then(r => r.ok ? r.json() : null)
                          .then(data => {
  if (data.occupied) {
    setSeatInfo(`Asiento ${seatNumber} ocupado por ${data.username}`);
  } else {
    setSeatInfo(`Asiento ${seatNumber} disponible`);
  }
  setSelectedSeat(seatNumber);
  setShowSeatModal(true);
});
                      }}
                      className={`w-11 h-11 rounded flex items-center justify-center text-sm font-bold cursor-pointer ${
                        isTaken
                          ? "bg-red-500 text-white"
                          : "bg-green-600 text-white hover:bg-green-700"
                      }`}
                    >
                      {seatNumber}
                    </div>
                  );
                })}
              </div>

              {/* PASILLO */}
              <div className="w-8"></div>

              {/* LADO DERECHO (6) */}
              <div className="flex gap-2">
                {Array.from({ length: 6 }).map((_, seatIndex) => {
                  const seatNumber = rowIndex * 12 + seatIndex + 7;
                  if (seatNumber > selectedSession.room.totalSeats) return null;

                  const isTaken = takenSeats.includes(seatNumber);

                  return (
                    <div
                      key={seatNumber}
                      onClick={() => {
                        fetch(
                          `http://localhost:8080/api/tickets/session/${selectedSession.id}/seat/${seatNumber}`
                        )
                          .then(r => r.ok ? r.json() : null)
                          .then(data => {
  if (data.occupied) {
    setSeatInfo(`Asiento ${seatNumber} ocupado por ${data.username}`);
  } else {
    setSeatInfo(`Asiento ${seatNumber} disponible`);
  }
  setSelectedSeat(seatNumber);
  setShowSeatModal(true);
});
                      }}
                      className={`w-11 h-11 rounded flex items-center justify-center text-sm font-bold cursor-pointer ${
                        isTaken
                          ? "bg-red-500 text-white"
                          : "bg-green-600 text-white hover:bg-green-700"
                      }`}
                    >
                      {seatNumber}
                    </div>
                  );
                })}
              </div>
            </div>
          ))}
        </div>

{/*MODAL ASIENTOS*/}
        {showSeatModal && (
  <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
    <div className="bg-white rounded-lg shadow-lg p-6 w-96 text-center">
      <h3 className="text-xl font-bold mb-4">Información del asiento</h3>

      <p className="mb-4 text-lg">{seatInfo}</p>

      {/* ===== SI ESTÁ OCUPADO ===== */}
      {seatInfo.includes("ocupado") && (
        <>
          {/* INPUT NUEVO ASIENTO */}
          <input
            type="number"
            min={1}
            max={selectedSession.room.totalSeats}
            placeholder={`Nuevo asiento (1 - ${selectedSession.room.totalSeats})`}
            className="border p-2 w-full mb-3"
            value={newSeat}
            onChange={e => setNewSeat(e.target.value)}
          />

          {/* BOTONES PRINCIPALES */}
          {!confirmAction && (
            <>
              <button
                className="bg-yellow-500 text-white px-4 py-2 rounded w-full mb-2"
                onClick={() => {
                  const seatNum = Number(newSeat);

                  if (!seatNum) {
                    alert("Introduce un número válido");
                    return;
                  }

                  if (
                    seatNum < 1 ||
                    seatNum > selectedSession.room.totalSeats
                  ) {
                    alert("Asiento fuera del rango de la sala");
                    return;
                  }

                  if (takenSeats.includes(seatNum)) {
                    alert("Ese asiento ya está ocupado");
                    return;
                  }

                  setConfirmAction("CHANGE");
                }}
              >
                Cambiar asiento
              </button>

              <button
                className="bg-red-600 text-white px-4 py-2 rounded w-full"
                onClick={() => setConfirmAction("DELETE")}
              >
                Liberar asiento
              </button>
            </>
          )}

          {/* ===== CONFIRMACIÓN ===== */}
          {confirmAction && (
            <div className="mt-4 bg-gray-100 p-4 rounded">
              <p className="mb-4 font-semibold">
                {confirmAction === "CHANGE"
                  ? `¿Confirmar cambio al asiento ${newSeat}?`
                  : `¿Confirmar liberación del asiento ${selectedSeat}?`}
              </p>

              <div className="flex gap-3">
                <button
                  className="bg-green-600 text-white px-4 py-2 rounded w-full"
                  onClick={() => {
                    if (confirmAction === "CHANGE") {
                      fetch(
                        `http://localhost:8080/api/tickets/session/${selectedSession.id}/seat/${selectedSeat}/change/${newSeat}`,
                        { method: "PUT" }
                      ).then(() => {
                        alert("Asiento cambiado");
                      });
                    }

                    if (confirmAction === "DELETE") {
                      fetch(
                        `http://localhost:8080/api/tickets/session/${selectedSession.id}/seat/${selectedSeat}`,
                        { method: "DELETE" }
                      ).then(() => {
                        alert("Asiento liberado");
                      });
                    }

                    setConfirmAction(null);
                    setShowSeatModal(false);
                    setNewSeat("");

                    fetch(
                      `http://localhost:8080/api/tickets/session/${selectedSession.id}`
                    )
                      .then(r => r.json())
                      .then(data =>
                        setTakenSeats(data.map(t => t.seatNumber))
                      );
                  }}
                >
                  Confirmar
                </button>

                <button
                  className="bg-gray-400 text-white px-4 py-2 rounded w-full"
                  onClick={() => setConfirmAction(null)}
                >
                  Cancelar
                </button>
              </div>
            </div>
          )}
        </>
      )}

      {/* CERRAR */}
      <button
        className="mt-4 text-gray-600 underline"
        onClick={() => {
          setConfirmAction(null);
          setShowSeatModal(false);
        }}
      >
        Cerrar
      </button>
    </div>
  </div>
)}



      </div>
    )}
  </>
)}

{/* ===== STAR WARS ===== */}
{section === "STARWARS" && (
  <>
    <h2 className="text-2xl font-bold mb-4">Campaña Star Wars</h2>

    <button
      className="bg-yellow-500 text-black px-4 py-2 rounded mb-4"
      onClick={() => {
        setLoadingSw(true);
        fetch("http://localhost:8080/api/swapi/movies")
  .then(r => r.json())
  .then(data => {
    console.log("SWAPI DATA PARSED:", data);
    setSwMovies(Array.isArray(data) ? data : []);
    setLoadingSw(false);
  })
  .catch(err => {
    console.error(err);
    setSwMovies([]);
    setLoadingSw(false);
  });

      }}
    >
      Cargar películas de Star Wars
    </button>

    {loadingSw && <p>Cargando películas...</p>}

    <ul>
      {Array.isArray(swMovies) && swMovies.map(movie => (
        <li
          key={movie.title}
          className="bg-white p-3 mb-2 rounded shadow flex justify-between items-center"
        >
          <div className="flex items-center gap-4">
  <img
    src={`https://starwars-visualguide.com/assets/img/films/${movie.episode_id}.jpg`}
    alt={movie.title}
    className="w-20 h-28 object-cover rounded"
  />

  <div>
    <p className="font-bold">{movie.title}</p>
    <p className="text-sm text-gray-600">
      Estreno: {movie.release_date}
    </p>
  </div>
</div>

          <button
  disabled={movieExists(movie.title)}
  className={`px-3 py-1 rounded text-white ${
    movieExists(movie.title)
      ? "bg-gray-400 cursor-not-allowed"
      : "bg-blue-600 hover:bg-blue-700"
  }`}
  onClick={() => {
    fetch("http://localhost:8080/api/swapi/import", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(movie)
    })
      .then(res => {
        if (!res.ok) throw new Error("La película ya existe");
      })
      .then(() => {
        alert("Película importada a CineVerse");
        fetch("http://localhost:8080/api/movies")
          .then(r => r.json())
          .then(setMovies);
      })
      .catch(err => alert(err.message));
  }}
>
  {movieExists(movie.title) ? "Ya importada" : "Importar"}
</button>
        </li>
      ))}
    </ul>
  </>
)}

      </main>
    </div>
  );
}

export default App;
