import { useEffect, useState } from "react";

function App() {
  // ===== AUTH =====
  const [currentUser, setCurrentUser] = useState(null);
  const [isRegister, setIsRegister] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // ===== UI =====
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [section, setSection] = useState("MOVIES");

  // ===== DATA =====
  const [movies, setMovies] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [sessions, setSessions] = useState([]);

  // ===== SESSION FORM =====
  const [movieId, setMovieId] = useState("");
  const [roomId, setRoomId] = useState("");
  const [dateTime, setDateTime] = useState("");

  // ===== MOVIE FORM =====
  const [newMovieTitle, setNewMovieTitle] = useState("");
  const [newMovieDescription, setNewMovieDescription] = useState("");
  const [newMovieDuration, setNewMovieDuration] = useState("");
  const [newMovieImageUrl, setNewMovieImageUrl] = useState("");

  // ===== ROOM FORM =====
  const [newRoomName, setNewRoomName] = useState("");
  const [newRoomSeats, setNewRoomSeats] = useState("");

  // ===== TICKETS =====
  const [selectedSession, setSelectedSession] = useState(null);
  const [takenSeats, setTakenSeats] = useState([]);

// ===== STAR WARS =====
const [swMovies, setSwMovies] = useState([]);
const [loadingSw, setLoadingSw] = useState(false);

  // ===== HELPERS =====
  const formatDateTime = (dateTimeString) => {
    const date = new Date(dateTimeString);
    return date.toLocaleString("es-ES", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
      hour: "2-digit",
      minute: "2-digit"
    });
  };

  // ===== LOGIN =====
  const login = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/api/users/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    })
      .then(res => {
        if (!res.ok) throw new Error();
        return res.json();
      })
      .then(user => {
        setCurrentUser(user);
        setError("");
      })
      .catch(() => setError("Credenciales incorrectas"));
  };

  // ===== REGISTER =====
  const register = (e) => {
    e.preventDefault();
    fetch("http://localhost:8080/api/users/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password })
    }).then(() => {
      setIsRegister(false);
      setError("Usuario creado, ahora inicia sesi√≥n");
    });
  };

  // ===== LOGOUT =====
  const logout = () => {
    setCurrentUser(null);
    setSidebarOpen(false);
    setUsername("");
    setPassword("");
  };

  // ===== LOAD DATA =====
  useEffect(() => {
    if (currentUser) {
      fetch("http://localhost:8080/api/movies").then(r => r.json()).then(setMovies);
      fetch("http://localhost:8080/api/rooms").then(r => r.json()).then(setRooms);
      fetch("http://localhost:8080/api/sessions").then(r => r.json()).then(setSessions);
    }
  }, [currentUser]);

  // ===== CREATE SESSION =====
  const createSession = (e) => {
    e.preventDefault();
    fetch(
      `http://localhost:8080/api/sessions?movieId=${movieId}&roomId=${roomId}&dateTime=${dateTime}`,
      { method: "POST" }
    ).then(() => {
      setMovieId("");
      setRoomId("");
      setDateTime("");
      fetch("http://localhost:8080/api/sessions")
        .then(r => r.json())
        .then(setSessions);
    });
  };

  // ===== LOGIN UI =====
  if (!currentUser) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <form onSubmit={isRegister ? register : login}
          className="bg-white p-6 rounded shadow w-80">
          <h2 className="text-xl font-bold mb-4">
            {isRegister ? "Registro" : "Login"}
          </h2>
          {error && <p className="text-red-500 mb-2">{error}</p>}
          <input className="border p-2 w-full mb-2"
            placeholder="Usuario"
            value={username}
            onChange={e => setUsername(e.target.value)} />
          <input className="border p-2 w-full mb-4"
            type="password"
            placeholder="Contrase√±a"
            value={password}
            onChange={e => setPassword(e.target.value)} />
          <button className="bg-blue-600 text-white w-full p-2 rounded mb-2">
            {isRegister ? "Crear cuenta" : "Entrar"}
          </button>
          <p className="text-sm text-center text-blue-600 cursor-pointer"
            onClick={() => { setIsRegister(!isRegister); setError(""); }}>
            {isRegister ? "¬øYa tienes cuenta?" : "¬øNo tienes cuenta?"}
          </p>
        </form>
      </div>
    );
  }

  // ===== APP =====
  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-gray-900 text-white p-4 flex justify-between items-center">
        <button onClick={() => setSidebarOpen(!sidebarOpen)}>‚ò∞</button>
        <span>{currentUser.username} ({currentUser.role})</span>
      </header>

      {sidebarOpen && (
        <aside className="fixed top-0 left-0 h-full w-64 bg-gray-800 text-white p-4 z-50">
          <h2 className="text-xl font-bold mb-6">CineVerse</h2>

          <div className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => { setSection("MOVIES"); setSidebarOpen(false); }}>
            Pel√≠culas
          </div>

          <div className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => { setSection("ROOMS"); setSidebarOpen(false); }}>
            Salas
          </div>

          <div className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => { setSection("SESSIONS"); setSidebarOpen(false); }}>
            Sesiones
          </div>

          <div className="p-2 hover:bg-gray-700 cursor-pointer"
            onClick={() => { setSection("TICKETS"); setSidebarOpen(false); }}>
            Entradas
          </div>

	{currentUser.role === "ADMIN" && (
  <div
    className="p-2 hover:bg-gray-700 cursor-pointer"
    onClick={() => { setSection("STARWARS"); setSidebarOpen(false); }}
  >
    Star Wars
  </div>
)}


          <div className="p-2 bg-red-600 mt-6 cursor-pointer"
            onClick={logout}>
            Cerrar sesi√≥n
          </div>

        </aside>
      )}

      <main className="p-6">

        {/* ===== MOVIES ===== */}
        {section === "MOVIES" && (
          <>
            <h2 className="text-2xl font-bold mb-4">Pel√≠culas</h2>

            {currentUser.role === "ADMIN" && (
              <form className="bg-white p-4 rounded shadow mb-6"
                onSubmit={e => {
                  e.preventDefault();
                  fetch("http://localhost:8080/api/movies", {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({
                      title: newMovieTitle,
                      description: newMovieDescription,
                      duration: newMovieDuration,
                      imageUrl: newMovieImageUrl
                    })
                  }).then(() => {
                    setNewMovieTitle("");
                    setNewMovieDescription("");
                    setNewMovieDuration("");
                    setNewMovieImageUrl("");
                    fetch("http://localhost:8080/api/movies")
                      .then(r => r.json())
                      .then(setMovies);
                  });
                }}>
                <h3 className="font-bold mb-2">A√±adir pel√≠cula</h3>
                <input className="border p-2 w-full mb-2" placeholder="T√≠tulo"
                  value={newMovieTitle} onChange={e => setNewMovieTitle(e.target.value)} />
                <input className="border p-2 w-full mb-2" type="number"
                  placeholder="Duraci√≥n"
                  value={newMovieDuration} onChange={e => setNewMovieDuration(e.target.value)} />
                <input className="border p-2 w-full mb-2" placeholder="URL imagen"
                  value={newMovieImageUrl} onChange={e => setNewMovieImageUrl(e.target.value)} />
                <textarea className="border p-2 w-full mb-2" placeholder="Descripci√≥n"
                  value={newMovieDescription}
                  onChange={e => setNewMovieDescription(e.target.value)} />
                <button className="bg-blue-600 text-white px-4 py-2 rounded">
                  A√±adir pel√≠cula
                </button>
              </form>
            )}

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {movies.map(movie => (
                <div key={movie.id} className="bg-white rounded shadow overflow-hidden">
                  {movie.imageUrl && (
                    <img src={movie.imageUrl} alt={movie.title}
                      className="w-full h-48 object-cover" />
                  )}
                  <div className="p-4">
                    <h3 className="text-xl font-bold">{movie.title}</h3>
                    <p className="text-sm text-gray-600">{movie.duration} min</p>
                    <p className="mt-2">{movie.description}</p>

                    {currentUser.role === "ADMIN" && (
                      <button
                        className="bg-red-500 text-white px-3 py-1 rounded mt-3"
                        onClick={() => {
                          if (!window.confirm("¬øEliminar esta pel√≠cula?")) return;
                          fetch(`http://localhost:8080/api/movies/${movie.id}`, {
                            method: "DELETE"
                          }).then(() =>
                            setMovies(movies.filter(m => m.id !== movie.id))
                          );
                        }}
                      >
                        Eliminar
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </>
        )}

        {/* ===== ROOMS ===== */}
        {section === "ROOMS" && (
          <>
            <h2 className="text-2xl font-bold mb-4">Salas</h2>

            {currentUser.role === "ADMIN" && (
              <form className="bg-white p-4 rounded shadow mb-6"
                onSubmit={e => {
                  e.preventDefault();
                  fetch("http://localhost:8080/api/rooms", {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({
                      name: newRoomName,
                      totalSeats: newRoomSeats
                    })
                  }).then(() => {
                    setNewRoomName("");
                    setNewRoomSeats("");
                    fetch("http://localhost:8080/api/rooms")
                      .then(r => r.json())
                      .then(setRooms);
                  });
                }}>
                <h3 className="font-bold mb-2">Crear sala</h3>
                <input className="border p-2 w-full mb-2"
                  placeholder="Nombre"
                  value={newRoomName}
                  onChange={e => setNewRoomName(e.target.value)} />
                <input className="border p-2 w-full mb-2"
                  type="number"
                  placeholder="Asientos"
                  value={newRoomSeats}
                  onChange={e => setNewRoomSeats(e.target.value)} />
                <button className="bg-blue-600 text-white px-4 py-2 rounded">
                  Crear sala
                </button>
              </form>
            )}

            <ul>
              {rooms.map(room => (
                <li key={room.id}
                  className="bg-white p-3 mb-2 rounded shadow flex justify-between">
                  <span>{room.name} ‚Äî {room.totalSeats} asientos</span>

                  {currentUser.role === "ADMIN" && (
                    <button
                      className="bg-red-500 text-white px-3 py-1 rounded"
                      onClick={() => {
                        if (!window.confirm("¬øEliminar sala?")) return;
                        fetch(`http://localhost:8080/api/rooms/${room.id}`, {
                          method: "DELETE"
                        }).then(() => {
   setRooms(rooms.filter(r => r.id !== room.id));
  fetch("http://localhost:8080/api/sessions")
    .then(r => r.json())
    .then(setSessions);
});
                      }}
                    >
                      Eliminar
                    </button>
                  )}
                </li>
              ))}
            </ul>
          </>
        )}

        {/* ===== SESSIONS ===== */}
        {section === "SESSIONS" && (
          <>
            <h2 className="text-2xl font-bold mb-4">Sesiones</h2>

            {currentUser.role === "ADMIN" && (
              <form onSubmit={createSession} className="bg-white p-4 rounded shadow mb-6">
                <select className="border p-2 w-full mb-2"
                  value={movieId} onChange={e => setMovieId(e.target.value)} required>
                  <option value="">Pel√≠cula</option>
                  {movies.map(m => (
                    <option key={m.id} value={m.id}>{m.title}</option>
                  ))}
                </select>

                <select className="border p-2 w-full mb-2"
                  value={roomId} onChange={e => setRoomId(e.target.value)} required>
                  <option value="">Sala</option>
                  {rooms.map(r => (
                    <option key={r.id} value={r.id}>{r.name}</option>
                  ))}
                </select>

                <input className="border p-2 w-full mb-2" type="datetime-local"
                  value={dateTime} onChange={e => setDateTime(e.target.value)} required />

                <button className="bg-blue-600 text-white px-4 py-2 rounded">
                  Crear sesi√≥n
                </button>
              </form>
            )}

            <ul>
              {sessions.map(s => (
                <li key={s.id}
                  className="bg-white p-3 mb-2 rounded shadow flex justify-between">
                  <span>
                    üé¨ {s.movie.title} ‚Äî üè¢ {s.room.name} ‚Äî üìÖ {formatDateTime(s.dateTime)}
                  </span>

                  {currentUser.role === "ADMIN" && (
                    <button
                      className="bg-red-500 text-white px-3 py-1 rounded"
                      onClick={() => {
                        if (!window.confirm("¬øEliminar sesi√≥n?")) return;
                        fetch(`http://localhost:8080/api/sessions/${s.id}`, {
                          method: "DELETE"
                        }).then(() =>
                          setSessions(sessions.filter(x => x.id !== s.id))
                        );
                      }}
                    >
                      Eliminar
                    </button>
                  )}
                </li>
              ))}
            </ul>
          </>
        )}

        {/* ===== TICKETS ===== */}
        {section === "TICKETS" && (
          <>
            <h2 className="text-2xl font-bold mb-4">Comprar entrada</h2>

            <select className="border p-2 mb-4"
              value={selectedSession?.id || ""}
              onChange={e => {
                const session = sessions.find(s => s.id === Number(e.target.value));
                setSelectedSession(session);
                fetch(`http://localhost:8080/api/tickets/session/${session.id}`)
                  .then(r => r.json())
                  .then(data => setTakenSeats(data.map(t => t.seatNumber)));
              }}>
              <option value="">Selecciona sesi√≥n</option>
              {sessions.map(s => (
                <option key={s.id} value={s.id}>
                  {s.movie.title} - {formatDateTime(s.dateTime)}
                </option>
              ))}
            </select>

            {selectedSession && (
              <div className="grid grid-cols-10 gap-2 max-w-md">
                {Array.from(
                  { length: selectedSession.room.totalSeats },
                  (_, i) => i + 1
                ).map(seat => (
                  <button key={seat}
                    disabled={takenSeats.includes(seat)}
                    className={`p-2 rounded text-white ${
                      takenSeats.includes(seat)
                        ? "bg-red-500 cursor-not-allowed"
                        : "bg-green-600"
                    }`}
                    onClick={() => {
                      fetch(
                        `http://localhost:8080/api/tickets?sessionId=${selectedSession.id}&seatNumber=${seat}&username=${currentUser.username}`,
                        { method: "POST" }
                      ).then(() => {
                        alert("Entrada comprada");
                        setTakenSeats([...takenSeats, seat]);
                      });
                    }}>
                    {seat}
                  </button>
                ))}
              </div>
            )}
          </>
        )}

{/* ===== STAR WARS ===== */}
{section === "STARWARS" && (
  <>
    <h2 className="text-2xl font-bold mb-4">Campa√±a Star Wars</h2>

    <button
      className="bg-yellow-500 text-black px-4 py-2 rounded mb-4"
      onClick={() => {
        setLoadingSw(true);
        fetch("http://localhost:8080/api/swapi/movies")
          .then(r => r.json())
          .then(data => {
            setSwMovies(data);
            setLoadingSw(false);
          });
      }}
    >
      Cargar pel√≠culas de Star Wars
    </button>

    {loadingSw && <p>Cargando pel√≠culas...</p>}

    <ul>
      {swMovies.map(movie => (
        <li
          key={movie.title}
          className="bg-white p-3 mb-2 rounded shadow flex justify-between items-center"
        >
          <span>
            {movie.title} ({movie.releaseDate})
          </span>

          <button
            className="bg-blue-600 text-white px-3 py-1 rounded"
            onClick={() => {
              fetch("http://localhost:8080/api/swapi/import", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(movie)
              }).then(() => {
                alert("Pel√≠cula importada a CineVerse");
                fetch("http://localhost:8080/api/movies")
                  .then(r => r.json())
                  .then(setMovies);
              });
            }}
          >
            Importar
          </button>
        </li>
      ))}
    </ul>
  </>
)}

      </main>
    </div>
  );
}

export default App;
