import React, { useEffect, useState } from "react";
import { connectChat, sendMessage } from "./ChatService";

export default function ChatBox({ user }) {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const messagesEndRef = React.useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    connectChat(msg => {
      setMessages(prev => [...prev, msg]);
    });
  }, []);

  const handleSend = () => {
    if (text.trim()) {
      // Agregar mensaje localmente primero (tú lo escribiste)
      setMessages(prev => [...prev, {
        sender: user.full_name,
        content: text,
        isMe: true
      }]);
      sendMessage(user.full_name, text);
      setText("");
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="flex flex-col h-full p-3">
      <div className="font-bold mb-2">Chat de soporte</div>

      <div className="h-48 overflow-y-auto mb-2 text-sm">
        {messages.map((m, i) => {
  const isMe = m.isMe === true;

  return (
    <div
      key={i}
      className={`mb-2 flex ${isMe ? "justify-end" : "justify-start"}`}
    >
      <div
        className={`px-3 py-2 rounded-lg text-sm max-w-[70%]
          ${isMe
            ? "bg-blue-600 text-white"
            : "bg-gray-300 text-gray-900"
          }`}
      >
        <div className="font-semibold text-xs mb-1">
          {isMe ? "Tú" : m.sender}
        </div>
        {m.content}
      </div>
    </div>
  );
})}
        <div ref={messagesEndRef} />
      </div>

      <input
        className="border p-2 w-full mb-1 rounded"
        value={text}
        onChange={e => setText(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="Escribe un mensaje..."
      />
      <button
        className="bg-blue-600 text-white w-full"
        onClick={handleSend}
      >
        Enviar
      </button>
    </div>
  );
}
