import { useEffect, useState } from "react";

function App() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/movies")
      .then(res => res.json())
      .then(data => setMovies(data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 flex">

      {/* Sidebar */}
      <aside className="w-64 bg-gray-900 text-white p-4">
        <h1 className="text-2xl font-bold mb-6">üé¨ CineVerse</h1>
        <nav className="space-y-2">
          <div className="hover:bg-gray-700 p-2 rounded cursor-pointer">Pel√≠culas</div>
          <div className="hover:bg-gray-700 p-2 rounded cursor-pointer">Salas</div>
          <div className="hover:bg-gray-700 p-2 rounded cursor-pointer">Sesiones</div>
          <div className="hover:bg-gray-700 p-2 rounded cursor-pointer">Entradas</div>
        </nav>
      </aside>

      {/* Main content */}
      <main className="flex-1 p-6">
        <h2 className="text-3xl font-semibold mb-4">Pel√≠culas en cartelera</h2>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {movies.map(movie => (
            <div key={movie.id} className="bg-white p-4 rounded shadow">
              <h3 className="text-xl font-bold mb-2">{movie.title}</h3>
              <p className="text-sm text-gray-600 mb-2">
                Duraci√≥n: {movie.duration} min
              </p>
              <p className="text-gray-700">
                {movie.description}
              </p>
            </div>
          ))}
        </div>

        {movies.length === 0 && (
          <p className="text-gray-500">No hay pel√≠culas registradas.</p>
        )}
      </main>

    </div>
  );
}

export default App;
