import { useEffect, useState } from "react";

function App() {
  // 游댏 Usuario logueado
  const [currentUser, setCurrentUser] = useState(null);

  // Navegaci칩n
  const [section, setSection] = useState("MOVIES");

  // Pel칤culas
  const [movies, setMovies] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [duration, setDuration] = useState("");
  const [imageUrl, setImageUrl] = useState("");

  // Salas
  const [rooms, setRooms] = useState([]);
  const [roomName, setRoomName] = useState("");
  const [totalSeats, setTotalSeats] = useState("");

  // Cargar datos
  const loadMovies = () => {
    fetch("http://localhost:8080/api/movies")
      .then(res => res.json())
      .then(data => setMovies(data));
  };

  const loadRooms = () => {
    fetch("http://localhost:8080/api/rooms")
      .then(res => res.json())
      .then(data => setRooms(data));
  };

  useEffect(() => {
    loadMovies();
    loadRooms();
  }, []);

  // Crear pel칤cula (ADMIN)
  const createMovie = (e) => {
    e.preventDefault();

    fetch("http://localhost:8080/api/movies", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Role": currentUser.role
      },
      body: JSON.stringify({
        title,
        description,
        duration: parseInt(duration),
        imageUrl
      })
    }).then(() => {
      setTitle("");
      setDescription("");
      setDuration("");
      setImageUrl("");
      loadMovies();
    });
  };

  // Crear sala (ADMIN)
  const createRoom = (e) => {
    e.preventDefault();

    fetch("http://localhost:8080/api/rooms", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        name: roomName,
        totalSeats: parseInt(totalSeats)
      })
    }).then(() => {
      setRoomName("");
      setTotalSeats("");
      loadRooms();
    });
  };

  // LOGIN
  if (!currentUser) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="bg-white p-6 rounded shadow w-80">
          <h2 className="text-xl font-bold mb-4">Login</h2>

          <button
            className="w-full bg-blue-600 text-white p-2 rounded mb-2"
            onClick={() => setCurrentUser({ username: "admin", role: "ADMIN" })}
          >
            Entrar como ADMIN
          </button>

          <button
            className="w-full bg-gray-600 text-white p-2 rounded"
            onClick={() => setCurrentUser({ username: "user", role: "USER" })}
          >
            Entrar como USUARIO
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 flex">

      {/* Sidebar */}
      <aside className="w-64 bg-gray-900 text-white p-4">
        <h1 className="text-2xl font-bold mb-6">游꿟 CineVerse</h1>
        <nav className="space-y-2">
          <div
            className="hover:bg-gray-700 p-2 rounded cursor-pointer"
            onClick={() => setSection("MOVIES")}
          >
            Pel칤culas
          </div>
          <div
            className="hover:bg-gray-700 p-2 rounded cursor-pointer"
            onClick={() => setSection("ROOMS")}
          >
            Salas
          </div>
        </nav>
      </aside>

      {/* Main */}
      <main className="flex-1 p-6">

        {/* ===== PEL칈CULAS ===== */}
        {section === "MOVIES" && (
          <>
            <h2 className="text-3xl font-semibold mb-4">Gesti칩n de Pel칤culas</h2>

            {currentUser.role === "ADMIN" && (
              <form onSubmit={createMovie} className="bg-white p-4 rounded shadow mb-6">
                <h3 className="text-xl font-bold mb-4">A침adir pel칤cula</h3>

                <input className="border p-2 w-full mb-2" placeholder="T칤tulo"
                  value={title} onChange={e => setTitle(e.target.value)} required />

                <textarea className="border p-2 w-full mb-2" placeholder="Descripci칩n"
                  value={description} onChange={e => setDescription(e.target.value)} required />

                <input className="border p-2 w-full mb-2" type="number" placeholder="Duraci칩n"
                  value={duration} onChange={e => setDuration(e.target.value)} required />

                <input className="border p-2 w-full mb-2" placeholder="URL imagen"
                  value={imageUrl} onChange={e => setImageUrl(e.target.value)} required />

                <button className="bg-blue-600 text-white px-4 py-2 rounded">
                  Guardar pel칤cula
                </button>
              </form>
            )}

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {movies.map(movie => (
                <div key={movie.id} className="bg-white rounded shadow overflow-hidden">
                  <img src={movie.imageUrl} alt={movie.title}
                    className="w-full h-48 object-cover" />
                  <div className="p-4">
                    <h3 className="text-xl font-bold">{movie.title}</h3>
                    <p className="text-sm">{movie.duration} min</p>
                    <p>{movie.description}</p>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}

        {/* ===== SALAS ===== */}
        {section === "ROOMS" && (
          <>
            <h2 className="text-3xl font-semibold mb-4">Gesti칩n de Salas</h2>

            {currentUser.role === "ADMIN" && (
              <form onSubmit={createRoom} className="bg-white p-4 rounded shadow mb-6">
                <h3 className="text-xl font-bold mb-4">A침adir sala</h3>

                <input className="border p-2 w-full mb-2" placeholder="Nombre de la sala"
                  value={roomName} onChange={e => setRoomName(e.target.value)} required />

                <input className="border p-2 w-full mb-2" type="number" placeholder="N칰mero de butacas"
                  value={totalSeats} onChange={e => setTotalSeats(e.target.value)} required />

                <button className="bg-blue-600 text-white px-4 py-2 rounded">
                  Guardar sala
                </button>
              </form>
            )}

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {rooms.map(room => (
                <div key={room.id} className="bg-white p-4 rounded shadow">
                  <h3 className="text-xl font-bold">{room.name}</h3>
                  <p>Butacas: {room.totalSeats}</p>
                </div>
              ))}
            </div>
          </>
        )}

      </main>
    </div>
  );
}

export default App;
