import React, { useState } from "react";
import { postData, deleteData, fetchData } from "../../utils/api";

export default function LoginForm({ onLoginSuccess, isRegister, setIsRegister }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    postData("http://localhost:8080/api/users/login", { username, password })
      .then(res => {
        if (!res.ok) throw new Error();
        return res.json();
      })
      .then(user => {
        onLoginSuccess(user);
        setError("");
      })
      .catch(() => setError("Credenciales incorrectas"));
  };

  const handleRegister = (e) => {
    e.preventDefault();
    postData("http://localhost:8080/api/users/register", { username, password })
      .then(() => {
        setIsRegister(false);
        setError("Usuario creado, ahora inicia sesión");
      });
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <form onSubmit={isRegister ? handleRegister : handleLogin}
        className="bg-white p-6 rounded shadow w-80">
        <h2 className="text-xl font-bold mb-4">
          {isRegister ? "Registro" : "Login"}
        </h2>
        {error && <p className="text-red-500 mb-2">{error}</p>}
        <input className="border p-2 w-full mb-2"
          placeholder="Usuario"
          value={username}
          onChange={e => setUsername(e.target.value)} />
        <input className="border p-2 w-full mb-4"
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={e => setPassword(e.target.value)} />
        <button className="bg-blue-600 text-white w-full p-2 rounded mb-2">
          {isRegister ? "Crear cuenta" : "Entrar"}
        </button>
        <p className="text-sm text-center text-blue-600 cursor-pointer"
          onClick={() => { setIsRegister(!isRegister); setError(""); }}>
          {isRegister ? "¿Ya tienes cuenta?" : "¿No tienes cuenta?"}
        </p>
      </form>
    </div>
  );
}
